import { Pool, neonConfig } from '@neondatabase/serverless';
import { drizzle } from 'drizzle-orm/neon-serverless';
import ws from "ws";
import * as schema from "@shared/schema";

neonConfig.webSocketConstructor = ws;

if (!process.env.DATABASE_URL) {
  throw new Error(
    "DATABASE_URL must be set. Did you forget to provision a database?",
  );
}

export const pool = new Pool({ connectionString: process.env.DATABASE_URL });
export const db = drizzle({ client: pool, schema });

server/index.ts.content:
import express, { type Request, Response, NextFunction } from "express";
import { registerRoutes } from "./routes";
import { setupVite, serveStatic, log } from "./vite";

const app = express();
app.use(express.json());
app.use(express.urlencoded({ extended: false }));

app.use((req, res, next) => {
  const start = Date.now();
  const path = req.path;
  let capturedJsonResponse: Record<string, any> | undefined = undefined;

  const originalResJson = res.json;
  res.json = function (bodyJson, ...args) {
    capturedJsonResponse = bodyJson;
    return originalResJson.apply(res, [bodyJson, ...args]);
  };

  res.on("finish", () => {
    const duration = Date.now() - start;
    if (path.startsWith("/api")) {
      let logLine = `${req.method} ${path} ${res.statusCode} in ${duration}ms`;
      if (capturedJsonResponse) {
        logLine += ` :: ${JSON.stringify(capturedJsonResponse)}`;
      }

      if (logLine.length > 80) {
        logLine = logLine.slice(0, 79) + "‚Ä¶";
      }

      log(logLine);
    }
  });

  next();
});

(async () => {
  const server = await registerRoutes(app);

  app.use((err: any, _req: Request, res: Response, _next: NextFunction) => {
    const status = err.status || err.statusCode || 500;
    const message = err.message || "Internal Server Error";

    res.status(status).json({ message });
    throw err;
  });

  // importantly only setup vite in development and after
  // setting up all the other routes so the catch-all route
  // doesn't interfere with the other routes
  if (app.get("env") === "development") {
    await setupVite(app, server);
  } else {
    serveStatic(app);
  }

  // ALWAYS serve the app on the port specified in the environment variable PORT
  // Other ports are firewalled. Default to 5000 if not specified.
  // this serves both the API and the client.
  // It is the only port that is not firewalled.
  const port = parseInt(process.env.PORT || '5000', 10);
  server.listen({
    port,
    host: "0.0.0.0",
    reusePort: true,
  }, () => {
    log(`serving on port ${port}`);
  });
})();

server/routes.ts.content:
import type { Express } from "express";
import { createServer, type Server } from "http";
import { WebSocketServer, WebSocket } from "ws";
import { storage } from "./storage";
import bcrypt from "bcryptjs";
import { 
  insertStaffSchema, 
  insertClientSchema,
  insertWeeklyDocumentationSchema,
  insertMonthlyReportSchema,
  insertCarePlanSchema,
  insertImplementationPlanSchema,
  insertVimsaTimeSchema,
  insertUserSchema,
  loginSchema,
  updateStaffSchema,
  updateWeeklyDocumentationSchema,
  updateMonthlyReportSchema,
  updateCarePlanSchema,
  updateImplementationPlanSchema,
  updateVimsaTimeSchema
} from "@shared/schema";

// WebSocket broadcasting function
let wss: WebSocketServer;

function broadcastUpdate(type: string, data: any) {
  if (!wss) return;
  const msg = JSON.stringify({ type, data });
  wss.clients.forEach(client => {
    if (client.readyState === WebSocket.OPEN) {
      client.send(msg);
    }
  });
}

export async function registerRoutes(app: Express): Promise<Server> {
  // Authentication routes
  app.post("/api/auth/login", async (req, res) => {
    try {
      const validated = loginSchema.parse(req.body);
      const user = await storage.getUserByUsername(validated.username);

      if (!user || !user.isActive) {
        return res.status(401).json({ message: "Ogiltigt anv√§ndarnamn eller l√∂senord" });
      }

      const isValidPassword = await bcrypt.compare(validated.password, user.passwordHash);
      if (!isValidPassword) {
        return res.status(401).json({ message: "Ogiltigt anv√§ndarnamn eller l√∂senord" });
      }

      // Don't send password hash to client
      const { passwordHash, ...userWithoutPassword } = user;

      res.json({ 
        user: userWithoutPassword,
        message: "Inloggning lyckades"
      });
    } catch (error) {
      res.status(400).json({ message: "Ogiltiga inloggningsuppgifter" });
    }
  });

  app.post("/api/auth/register", async (req, res) => {
    try {
      const validated = insertUserSchema.parse(req.body);

      // Hash password
      const saltRounds = 12;
      const passwordHash = await bcrypt.hash(validated.passwordHash, saltRounds);

      const userData = {
        ...validated,
        passwordHash,
      };

      const user = await storage.createUser(userData);
      const { passwordHash: _, ...userWithoutPassword } = user;

      res.status(201).json({ 
        user: userWithoutPassword,
        message: "Anv√§ndare skapad"
      });
    } catch (error) {
      res.status(400).json({ message: "Kunde inte skapa anv√§ndare" });
    }
  });

  app.post("/api/auth/change-password", async (req, res) => {
    try {
      const { currentPassword, newPassword, userId } = req.body;

      if (!userId) {
        return res.status(400).json({ message: "Anv√§ndar-ID kr√§vs" });
      }

      const user = await storage.getUserByUsername(userId);
      if (!user) {
        return res.status(404).json({ message: "Anv√§ndare hittades inte" });
      }

      const isValidPassword = await bcrypt.compare(currentPassword, user.passwordHash);
      if (!isValidPassword) {
        return res.status(401).json({ message: "Nuvarande l√∂senord √§r felaktigt" });
      }

      const saltRounds = 12;
      const newPasswordHash = await bcrypt.hash(newPassword, saltRounds);

      const updatedUser = await storage.updateUserPassword(user.id, newPasswordHash);
      if (!updatedUser) {
        return res.status(500).json({ message: "Kunde inte uppdatera l√∂senord" });
      }

      res.json({ message: "L√∂senord uppdaterat framg√•ngsrikt" });
    } catch (error) {
      res.status(500).json({ message: "Serverfel vid l√∂senords√§ndring" });
    }
  });

  // Staff routes
  app.get("/api/staff", async (req, res) => {
    try {
      const staff = await storage.getAllStaff();
      res.json(staff);
    } catch (error) {
      res.status(500).json({ message: "Failed to fetch staff" });
    }
  });

  // Search endpoint for staff
  app.get("/api/staff/search", async (req, res) => {
    try {
      const query = req.query.q as string;
      if (!query) {
        return res.json([]);
      }

      const allStaff = await storage.getAllStaff();
      const filteredStaff = allStaff.filter(staff => 
        staff.name.toLowerCase().includes(query.toLowerCase()) ||
        staff.initials.toLowerCase().includes(query.toLowerCase()) ||
        (staff.personnummer && staff.personnummer.toLowerCase().includes(query.toLowerCase())) ||
        (staff.telefon && staff.telefon.toLowerCase().includes(query.toLowerCase())) ||
        (staff.epost && staff.epost.toLowerCase().includes(query.toLowerCase())) ||
        (staff.roll && staff.roll.toLowerCase().includes(query.toLowerCase())) ||
        (staff.avdelning && staff.avdelning.toLowerCase().includes(query.toLowerCase()))
      );

      res.json(filteredStaff);
    } catch (error) {
      res.status(500).json({ message: "Kunde inte s√∂ka personal" });
    }
  });

  // Create staff
  app.post("/api/staff", async (req, res) => {
    try {
      const staffData = req.body;
      const staff = await storage.createStaff(staffData);
      broadcastUpdate('staff', staff);
      res.json(staff);
    } catch (error) {
      console.error("Error creating staff:", error);
      res.status(500).json({ message: "Kunde inte skapa personal" });
    }
  });

  // Delete staff
  app.delete("/api/staff/:id", async (req, res) => {
    try {
      const { id } = req.params;
      await storage.deleteStaff(id);
      broadcastUpdate('staff', { deleted: id });
      res.json({ message: "Personal borttagen" });
    } catch (error) {
      console.error("Error deleting staff:", error);
      res.status(500).json({ message: "Kunde inte ta bort personal" });
    }
  });

  // Client routes
  app.get("/api/clients/all", async (req, res) => {
    try {
      const clients = await storage.getAllClients();
      res.json(clients);
    } catch (error) {
      res.status(500).json({ message: "Kunde inte h√§mta klienter" });
    }
  });

  app.post("/api/clients", async (req, res) => {
    try {
      const clientData = req.body;
      const client = await storage.createClient(clientData);
      broadcastUpdate('clients', client);
      res.json(client);
    } catch (error) {
      console.error("Error creating client:", error);
      res.status(500).json({ message: "Kunde inte skapa klient" });
    }
  });

  app.delete("/api/clients/:id", async (req, res) => {
    try {
      const { id } = req.params;
      await storage.deleteClient(id);
      res.json({ message: "Klient borttagen" });
    } catch (error) {
      console.error("Error deleting client:", error);
      res.status(500).json({ message: "Kunde inte ta bort klient" });
    }
  });

  // Care plan routes
  app.get("/api/care-plans/all", async (req, res) => {
    try {
      const carePlans = await storage.getAllCarePlans();
      res.json(carePlans);
    } catch (error) {
      console.error("Error getting care plans:", error);
      res.status(500).json({ message: "Kunde inte h√§mta v√•rdplaner" });
    }
  });

  app.post("/api/care-plans", async (req, res) => {
    try {
      const carePlanData = req.body;
      const carePlan = await storage.createCarePlan(carePlanData);
      res.json(carePlan);
    } catch (error) {
      console.error("Error creating care plan:", error);
      res.status(500).json({ message: "Kunde inte skapa v√•rdplan" });
    }
  });

  // Weekly documentation routes
  app.get("/api/weekly-documentation/all", async (req, res) => {
    try {
      const docs = await storage.getAllWeeklyDocumentation();
      res.json(docs);
    } catch (error) {
      console.error("Error getting weekly documentation:", error);
      res.status(500).json({ message: "Kunde inte h√§mta veckodokumentation" });
    }
  });

  app.post("/api/weekly-documentation", async (req, res) => {
    try {
      const docData = req.body;
      const doc = await storage.createWeeklyDocumentation(docData);
      res.json(doc);
    } catch (error) {
      console.error("Error creating weekly documentation:", error);
      res.status(500).json({ message: "Kunde inte skapa veckodokumentation" });
    }
  });

  // Monthly report routes
  app.get("/api/monthly-reports/all", async (req, res) => {
    try {
      const reports = await storage.getAllMonthlyReports();
      res.json(reports);
    } catch (error) {
      console.error("Error getting monthly reports:", error);
      res.status(500).json({ message: "Kunde inte h√§mta m√•nadsrapporter" });
    }
  });

  app.post("/api/monthly-reports", async (req, res) => {
    try {
      const reportData = req.body;
      const report = await storage.createMonthlyReport(reportData);
      res.json(report);
    } catch (error) {
      console.error("Error creating monthly report:", error);
      res.status(500).json({ message: "Kunde inte skapa m√•nadsrapport" });
    }
  });

  app.get("/api/staff/:id", async (req, res) => {
    try {
      const staff = await storage.getStaff(req.params.id);
      if (!staff) {
        return res.status(404).json({ message: "Staff member not found" });
      }
      res.json(staff);
    } catch (error) {
      res.status(500).json({ message: "Failed to fetch staff member" });
    }
  });

  app.put("/api/staff/:id", async (req, res) => {
    try {
      const validatedData = updateStaffSchema.parse(req.body);
      const staff = await storage.updateStaff(req.params.id, validatedData);
      if (!staff) {
        return res.status(404).json({ message: "Staff member not found" });
      }
      broadcastUpdate('staff', staff);
      res.json(staff);
    } catch (error) {
      res.status(400).json({ message: "Invalid staff data", error });
    }
  });

  // Client routes - Updated to match frontend expectations
  app.get("/api/staff/:id/clients", async (req, res) => {
    try {
      const clients = await storage.getClientsByStaffId(req.params.id);
      res.json(clients);
    } catch (error) {
      console.error("Error fetching clients:", error);
      res.status(500).json({ message: "Kunde inte h√§mta klienter" });
    }
  });

  app.get("/api/clients/all", async (req, res) => {
    try {
      const clients = await storage.getAllClients();
      res.json(clients);
    } catch (error) {
      res.status(500).json({ message: "Kunde inte h√§mta alla klienter" });
    }
  });

  app.post("/api/clients", async (req, res) => {
    try {
      console.log("Creating client with data:", req.body);
      const validatedData = insertClientSchema.parse(req.body);
      const client = await storage.createClient(validatedData);
      res.status(201).json(client);
    } catch (error) {
      console.error("Error creating client:", error);
      res.status(400).json({ message: "Kunde inte skapa klient", error: String(error) });
    }
  });

  app.put("/api/clients/:id", async (req, res) => {
    try {
      const updates = req.body;
      const client = await storage.updateClient(req.params.id, updates);
      if (!client) {
        return res.status(404).json({ message: "Klient hittades inte" });
      }
      res.json(client);
    } catch (error) {
      res.status(400).json({ message: "Kunde inte uppdatera klient", error: String(error) });
    }
  });

  app.delete("/api/clients/:id", async (req, res) => {
    try {
      const deleted = await storage.deleteClient(req.params.id);
      if (!deleted) {
        return res.status(404).json({ message: "Klient hittades inte" });
      }
      res.json({ message: "Klient borttagen" });
    } catch (error) {
