import { 
  type User,
  type InsertUser,
  type Staff, 
  type InsertStaff, 
  type UpdateStaff,
  type Client,
  type InsertClient,
  type UpdateClient,
  type WeeklyDocumentation,
  type InsertWeeklyDocumentation,
  type UpdateWeeklyDocumentation,
  type MonthlyReport,
  type InsertMonthlyReport,
  type UpdateMonthlyReport,
  type CarePlan,
  type InsertCarePlan,
  type UpdateCarePlan,
  type ImplementationPlan,
  type InsertImplementationPlan,
  type UpdateImplementationPlan,
  type VimsaTime,
  type InsertVimsaTime,
  type UpdateVimsaTime
} from "@shared/schema";
import { randomUUID } from "crypto";

export interface IStorage {
  // User operations
  getUserByUsername(username: string): Promise<User | undefined>;
  getUserByEmail(email: string): Promise<User | undefined>;
  createUser(user: InsertUser): Promise<User>;
  updateUserPassword(userId: string, newPasswordHash: string): Promise<User | undefined>;

  // Staff operations
  getAllStaff(): Promise<Staff[]>;
  getStaff(id: string): Promise<Staff | undefined>;
  getStaffByName(name: string): Promise<Staff | undefined>;
  createStaff(staff: InsertStaff): Promise<Staff>;
  updateStaff(id: string, updates: UpdateStaff): Promise<Staff | undefined>;
  deleteStaff(id: string): Promise<boolean>;

  // Client operations
  getAllClients(): Promise<Client[]>;
  getClientsByStaffId(staffId: string): Promise<Client[]>;
  getClient(id: string): Promise<Client | undefined>;
  createClient(client: InsertClient): Promise<Client>;
  updateClient(id: string, updates: UpdateClient): Promise<Client | undefined>;
  deleteClient(id: string): Promise<boolean>;

  // Weekly documentation operations
  getAllWeeklyDocumentation(): Promise<WeeklyDocumentation[]>;
  getWeeklyDocumentation(clientId: string, year: number, week: number): Promise<WeeklyDocumentation | undefined>;
  createWeeklyDocumentation(doc: InsertWeeklyDocumentation): Promise<WeeklyDocumentation>;
  updateWeeklyDocumentation(id: string, updates: UpdateWeeklyDocumentation): Promise<WeeklyDocumentation | undefined>;

  // Monthly report operations
  getAllMonthlyReports(): Promise<MonthlyReport[]>;
  getMonthlyReport(clientId: string, year: number, month: number): Promise<MonthlyReport | undefined>;
  createMonthlyReport(report: InsertMonthlyReport): Promise<MonthlyReport>;
  updateMonthlyReport(id: string, updates: UpdateMonthlyReport): Promise<MonthlyReport | undefined>;

  // Care plan operations
  getAllCarePlans(): Promise<CarePlan[]>;
  getCarePlan(clientId: string): Promise<CarePlan | undefined>;
  createCarePlan(plan: InsertCarePlan): Promise<CarePlan>;
  updateCarePlan(id: string, updates: UpdateCarePlan): Promise<CarePlan | undefined>;
  deleteCarePlan(id: string): Promise<boolean>;

  // Implementation plan operations
  getAllImplementationPlans(): Promise<ImplementationPlan[]>;
  getImplementationPlan(clientId: string): Promise<ImplementationPlan | undefined>;
  createImplementationPlan(plan: InsertImplementationPlan): Promise<ImplementationPlan>;
  updateImplementationPlan(id: string, updates: UpdateImplementationPlan): Promise<ImplementationPlan | undefined>;

  // Vimsa time operations
  getAllVimsaTime(): Promise<VimsaTime[]>;
  getVimsaTime(clientId: string, year: number, week: number): Promise<VimsaTime | undefined>;
  createVimsaTime(time: InsertVimsaTime): Promise<VimsaTime>;
  updateVimsaTime(id: string, updates: UpdateVimsaTime): Promise<VimsaTime | undefined>;
}

export class MemStorage implements IStorage {
  private users: Map<string, User>;
  private staff: Map<string, Staff>;
  private clients: Map<string, Client>;
  private weeklyDocumentation: Map<string, WeeklyDocumentation>;
  private monthlyReports: Map<string, MonthlyReport>;
  private carePlans: Map<string, CarePlan>;
  private implementationPlans: Map<string, ImplementationPlan>;
  private vimsaTime: Map<string, VimsaTime>;


  constructor() {
    this.users = new Map();
    this.staff = new Map();
    this.clients = new Map();
    this.weeklyDocumentation = new Map();
    this.monthlyReports = new Map();
    this.carePlans = new Map();
    this.implementationPlans = new Map();
    this.vimsaTime = new Map();

    this.initializeDefaultData();
  }

  private async initializeDefaultData() {
    // Create default admin user
    try {
      const bcrypt = await import('bcryptjs');
      const adminPasswordHash = await bcrypt.default.hash('admin123', 12);

      const adminUser: User = {
        id: randomUUID(),
        username: 'admin',
        email: 'admin@ungdoms.se',
        passwordHash: adminPasswordHash,
        role: 'admin',
        isActive: true,
        createdAt: new Date(),
        updatedAt: new Date(),
      };
      this.users.set(adminUser.id, adminUser);
    } catch (error) {
      console.error('Failed to create default admin user:', error);
    }

    // Initialize default staff
    this.initializeDefaultStaff();
  }

  // User operations
  async getUserByUsername(username: string): Promise<User | undefined> {
    return Array.from(this.users.values()).find(user => user.username === username);
  }

  async getUserByEmail(email: string): Promise<User | undefined> {
    return Array.from(this.users.values()).find(user => user.email === email);
  }

  async createUser(userData: InsertUser): Promise<User> {
    const id = randomUUID();
    const now = new Date();
    const user: User = {
      id,
      username: userData.username,
      email: userData.email,
      passwordHash: userData.passwordHash,
      role: userData.role || "user",
      isActive: userData.isActive !== undefined ? userData.isActive : true,
      createdAt: now,
      updatedAt: now,
    };
    this.users.set(id, user);
    return user;
  }

  private getInitials(name: string): string {
    return name.split(' ').map(n => n[0]).join('');
  }

  private initializeDefaultStaff() {
    const defaultStaffNames = [
      "Afif Derbas", "Ahmed Alrakabi", "Ahmed Ramadan", "Ajmen Rafiq", "Alana Salah",
      "Alharis Albayati", "Amir Al-istarabadi", "Anjelika B√•√•th", "Bashdar Reza",
      "Constanza Soto", "Deni Dulji", "Diana Gharib", "Drilon Muqkurtaj", "Heidar Farhan",
      "Hussein Ahmed", "Ida Bj√∂rkbacka", "Ikhlas Almaliki", "Intisar Almansour",
      "Israa Touman", "Johan Wessberg", "Kaoula Channoufi", "Kim Torneus", "Lejla Kocacik",
      "Michelle Nilsson", "Mirza Celik", "Mirza Hodzic", "Nasima Kuraishe",
      "Nicolas Lazcano", "Omar Mezza", "Qasin Abdullahi", "Robert Ackar", "Samir Bezzina",
      "Sebastian Holm", "Wissam Hemissi", "Yasmin Ibrahim"
    ];

    defaultStaffNames.forEach(name => {
      const id = randomUUID();
      const staff: Staff = {
        id,
        name,
        initials: this.getInitials(name),
        personnummer: null,
        telefon: null,
        epost: null,
        adress: null,
        anst√§llningsdatum: null,
        roll: null,
        avdelning: null,
        createdAt: new Date(),
        updatedAt: new Date(),
      };
      this.staff.set(id, staff);
    });
  }

  // Staff operations
  async getAllStaff(): Promise<Staff[]> {
    return Array.from(this.staff.values()).sort((a, b) => a.name.localeCompare(b.name));
  }

  async getStaff(id: string): Promise<Staff | undefined> {
    return this.staff.get(id);
  }

  async getStaffByName(name: string): Promise<Staff | undefined> {
    return Array.from(this.staff.values()).find(staff => staff.name === name);
  }

  async createStaff(insertStaff: InsertStaff): Promise<Staff> {
    const id = randomUUID();
    const staff: Staff = {
      id,
      name: insertStaff.name,
      initials: insertStaff.initials,
      personnummer: insertStaff.personnummer || null,
      telefon: insertStaff.telefon || null,
      epost: insertStaff.epost || null,
      adress: insertStaff.adress || null,
      anst√§llningsdatum: insertStaff.anst√§llningsdatum || null,
      roll: insertStaff.roll || null,
      avdelning: insertStaff.avdelning || null,
      createdAt: new Date(),
      updatedAt: new Date(),
    };
    this.staff.set(id, staff);
    return staff;
  }

  async updateStaff(id: string, updates: UpdateStaff): Promise<Staff | undefined> {
    const existing = this.staff.get(id);
    if (!existing) return undefined;

    const updated: Staff = {
      ...existing,
      ...updates,
      updatedAt: new Date(),
    };
    this.staff.set(id, updated);
    return updated;
  }

  async deleteStaff(id: string): Promise<boolean> {
    return this.staff.delete(id);
  }

  // Client operations
  async getClientsByStaffId(staffId: string): Promise<Client[]> {
    return Array.from(this.clients.values())
      .filter(client => client.staffId === staffId)
      .sort((a, b) => a.initials.localeCompare(b.initials));
  }

  async getClient(id: string): Promise<Client | undefined> {
    return this.clients.get(id);
  }

  async createClient(insertClient: InsertClient): Promise<Client> {
    const id = randomUUID();
    const client: Client = {
      id,
      initials: insertClient.initials,
      staffId: insertClient.staffId,
      personalNumber: insertClient.personalNumber || null,
      notes: insertClient.notes || null,
      status: insertClient.status || null,
      createdAt: new Date(),
      updatedAt: new Date(),
    };
    this.clients.set(id, client);
    return client;
  }

  async updateClient(id: string, updates: UpdateClient): Promise<Client | undefined> {
    const existing = this.clients.get(id);
    if (!existing) return undefined;

    const updated: Client = {
      ...existing,
      ...updates,
      updatedAt: new Date(),
    };
    this.clients.set(id, updated);
    return updated;
  }

  async deleteClient(id: string): Promise<boolean> {
    return this.clients.delete(id);
  }

  async getAllClients(): Promise<Client[]> {
    return Array.from(this.clients.values());
  }

  // Weekly documentation operations
  async getWeeklyDocumentation(clientId: string, year: number, week: number): Promise<WeeklyDocumentation | undefined> {
    return Array.from(this.weeklyDocumentation.values())
      .find(doc => doc.clientId === clientId && doc.year === year && doc.week === week);
  }

  async createWeeklyDocumentation(insertDoc: InsertWeeklyDocumentation): Promise<WeeklyDocumentation> {
    const id = randomUUID();
    const doc: WeeklyDocumentation = {
      id,
      clientId: insertDoc.clientId,
      staffId: insertDoc.staffId,
      year: insertDoc.year,
      week: insertDoc.week,
      content: insertDoc.content || null,
      mondayStatus: insertDoc.mondayStatus || null,
      tuesdayStatus: insertDoc.tuesdayStatus || null,
      wednesdayStatus: insertDoc.wednesdayStatus || null,
      thursdayStatus: insertDoc.thursdayStatus || null,
      fridayStatus: insertDoc.fridayStatus || null,
      saturdayStatus: insertDoc.saturdayStatus || null,
      sundayStatus: insertDoc.sundayStatus || null,
      mondayDocumented: insertDoc.mondayDocumented !== undefined ? insertDoc.mondayDocumented : false,
      tuesdayDocumented: insertDoc.tuesdayDocumented !== undefined ? insertDoc.tuesdayDocumented : false,
      wednesdayDocumented: insertDoc.wednesdayDocumented !== undefined ? insertDoc.wednesdayDocumented : false,
      thursdayDocumented: insertDoc.thursdayDocumented !== undefined ? insertDoc.thursdayDocumented : false,
      fridayDocumented: insertDoc.fridayDocumented !== undefined ? insertDoc.fridayDocumented : false,
      saturdayDocumented: insertDoc.saturdayDocumented !== undefined ? insertDoc.saturdayDocumented : false,
      sundayDocumented: insertDoc.sundayDocumented !== undefined ? insertDoc.sundayDocumented : false,
      documentation: insertDoc.documentation || null,
      approved: insertDoc.approved !== undefined ? insertDoc.approved : false,
      comments: insertDoc.comments || null,
      createdAt: new Date(),
      updatedAt: new Date(),
    };
    this.weeklyDocumentation.set(id, doc);
    return doc;
  }

  async updateWeeklyDocumentation(id: string, updates: UpdateWeeklyDocumentation): Promise<WeeklyDocumentation | undefined> {
    const existing = this.weeklyDocumentation.get(id);
    if (!existing) return undefined;

    const updated: WeeklyDocumentation = {
      ...existing,
      ...updates,
      updatedAt: new Date(),
    };
    this.weeklyDocumentation.set(id, updated);
    return updated;
  }

  // Monthly report operations
  async getMonthlyReport(clientId: string, year: number, month: number): Promise<MonthlyReport | undefined> {
    return Array.from(this.monthlyReports.values())
      .find(report => report.year === year && report.month === month);
  }

  async createMonthlyReport(insertReport: InsertMonthlyReport): Promise<MonthlyReport> {
    const id = randomUUID();
    const report: MonthlyReport = {
      id,
      clientId: insertReport.clientId,
      staffId: insertReport.staffId,
      year: insertReport.year,
      month: insertReport.month,
      content: insertReport.content || null,
      reportContent: insertReport.reportContent || null,
      status: insertReport.status || null,
      comment: insertReport.comment || null,
      createdAt: new Date(),
      updatedAt: new Date(),
    };
    this.monthlyReports.set(id, report);
    return report;
  }

  async updateMonthlyReport(id: string, updates: UpdateMonthlyReport): Promise<MonthlyReport | undefined> {
    const existing = this.monthlyReports.get(id);
    if (!existing) return undefined;

    const updated: MonthlyReport = {
      ...existing,
      ...updates,
      updatedAt: new Date(),
    };
    this.monthlyReports.set(id, updated);
    return updated;
  }

  // Care plan operations
  async getCarePlan(clientId: string): Promise<CarePlan | undefined> {
    return Array.from(this.carePlans.values())
      .find(plan => plan.clientId === clientId);
  }

  async createCarePlan(insertPlan: InsertCarePlan): Promise<CarePlan> {
    const id = randomUUID();
    const plan: CarePlan = {
      id,
      clientId: insertPlan.clientId,
      staffId: insertPlan.staffId,
      planContent: insertPlan.planContent || null,
      goals: insertPlan.goals || null,
      interventions: insertPlan.interventions || null,
      evaluationCriteria: insertPlan.evaluationCriteria || null,
      receivedDate: insertPlan.receivedDate || null,
      enteredJournalDate: insertPlan.enteredJournalDate || null,
      staffNotifiedDate: insertPlan.staffNotifiedDate || null,
      status: insertPlan.status || null,
      isActive: insertPlan.isActive !== undefined ? insertPlan.isActive : null,
      comment: insertPlan.comment || null,
      createdAt: new Date(),
      updatedAt: new Date(),
    };
    this.carePlans.set(id, plan);
    return plan;
  }

  async updateCarePlan(id: string, updates: UpdateCarePlan): Promise<CarePlan | undefined> {
    const existing = this.carePlans.get(id);
    if (!existing) return undefined;

    const updated: CarePlan = {
      ...existing,
      ...updates,
      updatedAt: new Date(),
    };
    this.carePlans.set(id, updated);
    return updated;
  }

  async deleteCarePlan(id: string): Promise<boolean> {
    return this.carePlans.delete(id);
  }

  // Implementation plan operations
  async getImplementationPlan(clientId: string): Promise<ImplementationPlan | undefined> {
    return Array.from(this.implementationPlans.values())
      .find(plan => plan.clientId === clientId);
  }

  async createImplementationPlan(insertPlan: InsertImplementationPlan): Promise<ImplementationPlan> {
    const id = randomUUID();
    const plan: ImplementationPlan = {
      id,
      clientId: insertPlan.clientId,
      staffId: insertPlan.staffId,
      carePlanId: insertPlan.carePlanId || null,
      planContent: insertPlan.planContent || null,
      goals: insertPlan.goals || null,
      activities: insertPlan.activities || null,
      followUpSchedule: insertPlan.followUpSchedule || null,
      status: insertPlan.status || "pending",
      isActive: insertPlan.isActive !== undefined ? insertPlan.isActive : true,
      followup1: insertPlan.followup1 || false,
      followup2: insertPlan.followup2 || false,
      createdDate: insertPlan.createdDate || new Date(),
      comments: insertPlan.comments || null,
      createdAt: new Date(),
      updatedAt: new Date(),
    };
    this.implementationPlans.set(id, plan);
    return plan;
  }

  async updateImplementationPlan(id: string, updates: UpdateImplementationPlan): Promise<ImplementationPlan | undefined> {
    const existing = this.implementationPlans.get(id);
    if (!existing) return undefined;

    const updated: ImplementationPlan = {
      ...existing,
      ...updates,
      updatedAt: new Date(),
    };
    this.implementationPlans.set(id, updated);
    return updated;
  }

  // Vimsa time operations
  async getVimsaTime(clientId: string, year: number, week: number): Promise<VimsaTime | undefined> {
    return Array.from(this.vimsaTime.values())
      .find(time => time.clientId === clientId && time.year === year && time.week === week);
  }

  async createVimsaTime(insertTime: InsertVimsaTime): Promise<VimsaTime> {
    const id = randomUUID();
    const time: VimsaTime = {
      id,
      clientId: insertTime.clientId,
      staffId: insertTime.staffId,
      year: insertTime.year,
      week: insertTime.week,
      monday: insertTime.monday || 0,
      tuesday: insertTime.tuesday || 0,
      wednesday: insertTime.wednesday || 0,
      thursday: insertTime.thursday || 0,
      friday: insertTime.friday || 0,
      saturday: insertTime.saturday || 0,
      sunday: insertTime.sunday || 0,
      totalHours: insertTime.totalHours || 0,
      status: insertTime.status || null,
      approved: insertTime.approved || false,
      comments: insertTime.comments || null,
      createdAt: new Date(),
      updatedAt: new Date(),
    };
    this.vimsaTime.set(id, time);
    return time;
  }

  async updateVimsaTime(id: string, updates: UpdateVimsaTime): Promise<VimsaTime | undefined> {
    const existing = this.vimsaTime.get(id);
    if (!existing) return undefined;

    const updated: VimsaTime = {
      ...existing,
      ...updates,
      updatedAt: new Date(),
    };
    this.vimsaTime.set(id, updated);
    return updated;
  }

  // Get all care plans
  async getAllCarePlans(): Promise<CarePlan[]> {
    return Array.from(this.carePlans.values());
  }

  // Get all implementation plans
  async getAllImplementationPlans(): Promise<ImplementationPlan[]> {
    return Array.from(this.implementationPlans.values());
  }



  // Get all weekly documentation
  async getAllWeeklyDocumentation(): Promise<WeeklyDocumentation[]> {
    return Array.from(this.weeklyDocumentation.values());
  }

  // Get all monthly reports
  async getAllMonthlyReports(): Promise<MonthlyReport[]> {
    return Array.from(this.monthlyReports.values());
  }

  // Get all Vimsa time data
  async getAllVimsaTime(): Promise<VimsaTime[]> {
    return Array.from(this.vimsaTime.values());
  }

  // Update user password
  async updateUserPassword(userId: string, newPasswordHash: string): Promise<User | undefined> {
    const existing = this.users.get(userId);
    if (!existing) return undefined;

    const updated: User = {
      ...existing,
      passwordHash: newPasswordHash,
      updatedAt: new Date(),
    };
    this.users.set(userId, updated);
    return updated;
  }
}

export const storage = new MemStorage();

