import { Button } from "@/components/ui/button";
import { Dialog, DialogContent, DialogTrigger } from "@/components/ui/dialog";
import { Settings, LogOut } from "lucide-react";
import type { Staff, User } from "@shared/schema";

function MainApp() {
  const [sidebarOpen, setSidebarOpen] = useState(false);
  const [activeView, setActiveView] = useState('dashboard');
  const [activeStaffId, setActiveStaffId] = useState<string | null>(null);
  const [searchTerm, setSearchTerm] = useState('');
  const [currentUser, setCurrentUser] = useState<User | null>(null);
  const [isAuthenticated, setIsAuthenticated] = useState(false);

  const { data: staff = [], isLoading } = useQuery<Staff[]>({
    queryKey: ["/api/staff"],
    enabled: isAuthenticated, // Only fetch staff data when authenticated
  });

  const handleViewChange = (view: string, staffId?: string) => {
    setActiveView(view === 'staff' ? `staff-${staffId}` : view);
    setActiveStaffId(staffId || null);
  };

  const handleExport = async () => {
    try {
      window.open('/api/export', '_blank');
    } catch (error) {
      console.error('Export failed:', error);
    }
  };

  const handleLoginSuccess = (user: User) => {
    setCurrentUser(user);
    setIsAuthenticated(true);
  };

  const handleLogout = () => {
    setCurrentUser(null);
    setIsAuthenticated(false);
    setActiveView('dashboard');
    setActiveStaffId(null);
  };

  const activeStaff = activeStaffId ? staff.find(s => s.id === activeStaffId) : null;

  // Close sidebar when clicking outside on mobile
  useEffect(() => {
    const handleClickOutside = (e: MouseEvent) => {
      const target = e.target as Element;
      const sidebar = document.querySelector('aside');
      const sidebarToggle = document.querySelector('[data-sidebar-toggle]');

      if (window.innerWidth < 1024 && 
          sidebar && !sidebar.contains(target) && 
          sidebarToggle && !sidebarToggle.contains(target) &&
          sidebarOpen) {
        setSidebarOpen(false);
      }
    };

    document.addEventListener('click', handleClickOutside);
    return () => document.removeEventListener('click', handleClickOutside);
  }, [sidebarOpen]);

  // Show login form if not authenticated
  if (!isAuthenticated) {
    return <LoginForm onLoginSuccess={handleLoginSuccess} />;
  }

  if (isLoading) {
    return (
      <div className="min-h-screen bg-white flex items-center justify-center">
        <div className="text-center">
          <UngdomsLogo size="xl" className="justify-center mb-6" />
          <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-ungdoms-600 mx-auto mb-4"></div>
          <p className="text-ungdoms-600">Laddar v√•rdadministrativt system...</p>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-white">
      <Header
        onSidebarToggle={() => setSidebarOpen(!sidebarOpen)}
        onExport={handleExport}
        searchTerm={searchTerm}
        onSearchChange={setSearchTerm}
        currentUser={currentUser}
        onLogout={handleLogout}
      />

      <div className="flex h-screen pt-16">
        <StaffSidebar
          staff={staff}
          activeView={activeView}
          onViewChange={handleViewChange}
          isOpen={sidebarOpen}
          onClose={() => setSidebarOpen(false)}
          searchTerm={searchTerm}
        />

        <main className="flex-1 overflow-y-auto bg-white">
          <div className="p-6">
            {/* UNGDOMS Logo Header */}
            <div className="mb-6 text-center">
              <UngdomsLogo size="lg" className="justify-center" />
              <h1 className="mt-4 text-2xl font-bold text-ungdoms-700">
                √ñppenv√•rd Administrativt System
              </h1>
              <p className="text-muted-foreground mt-1">
                Klienthantering och uppf√∂ljning f√∂r v√•rdpersonal
              </p>
            </div>

            {activeView === 'dashboard' ? (
              <Dashboard />
            ) : activeStaff ? (
              <div className="space-y-6">
                <div className="bg-ungdoms-50 rounded-lg p-4 border border-ungdoms-200">
                  <h2 className="text-xl font-semibold text-ungdoms-800 mb-2">
                    {activeStaff.name} - {activeStaff.roll}
                  </h2>
                  <p className="text-ungdoms-600">
                    Avdelning: {activeStaff.avdelning} | Initialer: {activeStaff.initials}
                  </p>
                </div>

                <StaffClientManagement staff={activeStaff} />
              </div>
            ) : (
              <div className="text-center py-12">
                <div className="bg-ungdoms-50 rounded-lg p-8 border border-ungdoms-200 max-w-md mx-auto">
                  <p className="text-ungdoms-600 text-lg">
                    V√§lj en personalmedlem fr√•n sidopanelen f√∂r att b√∂rja
                  </p>
                  <p className="text-sm text-muted-foreground mt-2">
                    Systemet anv√§nder fl√∂desbaserad klienthantering
                  </p>
                </div>
              </div>
            )}
          </div>
        </main>
      </div>
    </div>
  );
}

function App() {
  return (
    <QueryClientProvider client={queryClient}>
      <TooltipProvider>
        <Toaster />
        <MainApp />
      </TooltipProvider>
    </QueryClientProvider>
  );
}

export default App;

