}

@layer base {
  * {
    @apply border-border;
  }

  body {
    @apply font-sans antialiased bg-background text-foreground;
    font-family: 'Inter', sans-serif;
  }
}

@layer components {
  /* UNGDOMS theme header */
  .ungdoms-header {
    background: linear-gradient(135deg, var(--ungdoms-500) 0%, var(--ungdoms-700) 100%);
    color: white;
  }

  /* Dokumentation status - Clean blue theme */
  .status-done {
    @apply bg-green-100 text-green-700 border-green-300;
  }

  .status-reminded {
    @apply bg-yellow-100 text-yellow-700 border-yellow-300;
  }

  .status-not-done {
    @apply bg-red-100 text-red-700 border-red-300;
  }

  /* Rapport status */
  .status-submitted {
    @apply bg-green-100 text-green-700 border-green-300;
  }

  .status-late {
    @apply bg-red-100 text-red-700 border-red-300;
  }

  .status-not-submitted {
    @apply bg-gray-100 text-gray-700 border-gray-300;
  }

  /* Kvalitet status */
  .status-approved {
    @apply bg-green-100 text-green-700 border-green-300;
  }

  .status-not-approved {
    @apply bg-red-100 text-red-700 border-red-300;
  }

  .status-pending {
    @apply bg-yellow-100 text-yellow-700 border-yellow-300;
  }

  /* Vimsa status */
  .status-accepted {
    @apply bg-green-100 text-green-700 border-green-300;
  }

  .status-rejected {
    @apply bg-red-100 text-red-700 border-red-300;
  }

  .sidebar-transition {
    transition: transform 0.3s ease-in-out;
  }

  .sidebar-hidden {
    transform: translateX(-100%);
  }

  .fade-in {
    animation: fadeIn 0.3s ease-in-out;
  }

  .table-hover tr:hover {
    background-color: hsl(0, 0%, 12%);
  }

  .ungdoms-bg {
    background-color: var(--ungdoms-500);
  }

  .ungdoms-bg-light {
    background-color: var(--ungdoms-800);
  }

  .ungdoms-text {
    color: var(--ungdoms-400);
  }

  .ungdoms-border {
    border-color: var(--ungdoms-600);
  }

  .ungdoms-logo-area {
    background: radial-gradient(circle at center, var(--ungdoms-900) 0%, transparent 70%);
  }
}

@keyframes fadeIn {
  from { 
    opacity: 0; 
    transform: translateY(10px); 
  }
  to { 
    opacity: 1; 
    transform: translateY(0); 
  }
}

@media (max-width: 768px) {
  .sidebar-hidden {
    transform: translateX(-100%);
  }
}

