import { useState } from "react";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { useMutation, useQuery } from "@tanstack/react-query";
import { z } from "zod";
import { queryClient } from "@/lib/queryClient";
import { useToast } from "@/hooks/use-toast";
import {
  Dialog,
  DialogContent,
  DialogHeader,
  DialogTitle,
  DialogTrigger,
} from "@/components/ui/dialog";
import {
  Form,
  FormControl,
  FormField,
  FormItem,
  FormLabel,
  FormMessage,
} from "@/components/ui/form";
import {
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from "@/components/ui/select";
import { Input } from "@/components/ui/input";
import { Textarea } from "@/components/ui/textarea";
import { Button } from "@/components/ui/button";
import { Plus, FileText } from "lucide-react";
import type { Staff, Client } from "@shared/schema";

const carePlanSchema = z.object({
  socialWorkerName: z.string().min(1, "Socialsekreterare m√•ste anges"),
  clientInitials: z.string().min(1, "Klientinitialer kr√§vs"),
  planNumber: z.string().min(1, "V√•rdplansnummer kr√§vs (1, 2, 3 etc)"),
  receivedDate: z.string().min(1, "Mottagningsdatum kr√§vs"),
  assignedStaffIds: z.array(z.string()).min(1, "Minst en personal m√•ste kopplas"),
  journalDate: z.string().optional(),
  comment: z.string().optional(),
});

type CarePlanFormData = z.infer<typeof carePlanSchema>;

interface CarePlanDialogProps {
  trigger?: React.ReactNode;
}

export function CarePlanDialog({ trigger }: CarePlanDialogProps) {
  const [isOpen, setIsOpen] = useState(false);
  const { toast } = useToast();

  const form = useForm<CarePlanFormData>({
    resolver: zodResolver(carePlanSchema),
    defaultValues: {
      socialWorkerName: "",
      clientInitials: "",
      planNumber: "",
      receivedDate: new Date().toISOString().split('T')[0],
      assignedStaffIds: [],
      journalDate: "",
      comment: "",
    },
  });

  // Fetch all staff
  const { data: staff = [] } = useQuery<Staff[]>({
    queryKey: ["/api/staff"],
  });

  // Create care plan mutation
  const createCarePlanMutation = useMutation({
    mutationFn: async (data: CarePlanFormData) => {
      console.log("Creating care plan with data:", data);

      try {
        // Create client first if doesn't exist (assign to first staff member)
        const primaryStaffId = data.assignedStaffIds[0];
        console.log("Primary staff ID:", primaryStaffId);

        const clientsResponse = await fetch(`/api/staff/${primaryStaffId}/clients`);
        if (!clientsResponse.ok) {
          throw new Error(`Failed to fetch clients: ${clientsResponse.status}`);
        }
        const clients: Client[] = await clientsResponse.json();

      let client = clients.find(c => c.initials === data.clientInitials);

      if (!client) {
        const newClientResponse = await fetch("/api/clients", {
          method: "POST",
          headers: { "Content-Type": "application/json" },
          body: JSON.stringify({
            initials: data.clientInitials,
            staffId: primaryStaffId,
            personalNumber: "",
            notes: `V√•rdplan ${data.planNumber} fr√•n ${data.socialWorkerName}`,
            status: "active",
          }),
        });

        if (!newClientResponse.ok) {
          const errorText = await newClientResponse.text();
          console.error("Client creation failed:", errorText);
          throw new Error(`Kunde inte skapa klient: ${errorText}`);
        }

        client = await newClientResponse.json();
        console.log("Created/found client:", client);
      }

      // Create the care plan
      console.log("Creating care plan...");
      const response = await fetch("/api/care-plans", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({
          clientId: client!.id,
          staffId: primaryStaffId,
          receivedDate: data.receivedDate,
          enteredJournalDate: data.journalDate || null,
          staffNotifiedDate: new Date().toISOString().split('T')[0],
          planContent: `V√•rdplan ${data.planNumber} fr√•n socialsekreterare ${data.socialWorkerName}`,
          goals: "Genomf√∂ra v√•rdfl√∂de enligt rutin",
          interventions: "Standard v√•rdfl√∂de - GFP ska p√•b√∂rjas inom 3 veckor",
          status: "staff_notified",
          comment: data.comment || "",
        }),
      });

      if (!response.ok) {
        const errorText = await response.text();
        console.error("Care plan creation failed:", errorText);
        throw new Error(`Kunde inte skapa v√•rdplan: ${errorText}`);
      }

      const carePlan = await response.json();
      console.log("Created care plan:", carePlan);

      // Create implementation plan automatically (this triggers GFP workflow)
      await fetch("/api/implementation-plans", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({
          clientId: client!.id,
          staffId: primaryStaffId,
          carePlanId: carePlan.id,
          planContent: `GFP f√∂r v√•rdplan ${data.planNumber}`,
          goals: "Genomf√∂randeplan enligt v√•rdplan",
          activities: "GFP aktiviteter - ska slutf√∂ras inom 3 veckor",
          followUpSchedule: "3 veckor fr√•n idag",
          status: "pending",
          isActive: true,
        }),
      });

      // Create initial weekly documentation template for current week
      const now = new Date();
      const currentWeek = Math.floor((now.getTime() - new Date(now.getFullYear(), 0, 1).getTime()) / (7 * 24 * 60 * 60 * 1000)) + 1;

      await fetch("/api/weekly-documentation", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({
          clientId: client!.id,
          staffId: primaryStaffId,
          year: now.getFullYear(),
          week: Math.max(33, Math.min(52, currentWeek)), // Ensure within valid range
          content: `Veckodokumentation f√∂r v√•rdplan ${data.planNumber}`,
          mondayStatus: "not_done",
          tuesdayStatus: "not_done", 
          wednesdayStatus: "not_done",
          thursdayStatus: "not_done",
          fridayStatus: "not_done",
          saturdayStatus: "not_done",
          sundayStatus: "not_done",
          mondayDocumented: false,
          tuesdayDocumented: false,
          wednesdayDocumented: false,
          thursdayDocumented: false,
          fridayDocumented: false,
          saturdayDocumented: false,
          sundayDocumented: false,
          documentation: "",
          approved: false,
          comments: "",
        }),
      });

      // Create initial monthly report for current month
      await fetch("/api/monthly-reports", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({
          clientId: client!.id,
          staffId: primaryStaffId,
          year: now.getFullYear(),
          month: now.getMonth() + 1,
          content: `M√•nadsrapport f√∂r v√•rdplan ${data.planNumber}`,
          reportContent: "",
          status: "not_started",
          comment: "",
        }),
      });

      // Create initial Vimsa time entry for current week
      await fetch("/api/vimsa-time", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({
          clientId: client!.id,
          staffId: primaryStaffId,
          year: now.getFullYear(),
          week: Math.max(33, Math.min(52, currentWeek)),
          monday: 0,
          tuesday: 0,
          wednesday: 0,
          thursday: 0,
          friday: 0,
          saturday: 0,
          sunday: 0,
          totalHours: 0,
          status: "not_started",
          approved: false,
          comments: `Vimsa tid f√∂r v√•rdplan ${data.planNumber}`,
        }),
      });

      return carePlan;
      } catch (error) {
        console.error("Error during care plan creation:", error);
        throw error;
      }
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/care-plans"] });
      queryClient.invalidateQueries({ queryKey: ["/api/clients"] });
      queryClient.invalidateQueries({ queryKey: ["/api/implementation-plans"] });

      toast({
        title: "V√•rdplan skapad",
        description: "V√•rdplanen har skapats och personal har notifierats. GFP ska g√∂ras inom 3 veckor.",
      });

      form.reset();
      setIsOpen(false);
    },
    onError: (error: Error) => {
      toast({
        title: "Fel",
        description: error.message || "Kunde inte skapa v√•rdplan",
        variant: "destructive",
      });
    },
  });

  const handleSubmit = (data: CarePlanFormData) => {
    createCarePlanMutation.mutate(data);
  };

  return (
    <Dialog open={isOpen} onOpenChange={setIsOpen}>
      <DialogTrigger asChild>
        {trigger || (
          <Button className="bg-ungdoms-600 hover:bg-ungdoms-700">
            <Plus className="h-4 w-4 mr-2" />
            Skapa V√•rdplan
          </Button>
        )}
      </DialogTrigger>
      <DialogContent className="sm:max-w-[600px]">
        <DialogHeader>
          <DialogTitle className="flex items-center gap-2">
            <FileText className="h-5 w-5 text-ungdoms-600" />
            Skapa ny v√•rdplan
          </DialogTitle>
        </DialogHeader>
        <Form {...form}>
          <form onSubmit={form.handleSubmit(handleSubmit)} className="space-y-4">
            <FormField
              control={form.control}
              name="socialWorkerName"
              render={({ field }) => (
                <FormItem>
                  <FormLabel>Socialsekreterare</FormLabel>
                  <FormControl>
                    <Input 
                      placeholder="Namn p√• socialsekreterare" 
                      {...field} 
                    />
                  </FormControl>
                  <FormMessage />
                </FormItem>
              )}
            />

            <div className="grid grid-cols-2 gap-4">
              <FormField
                control={form.control}
                name="planNumber"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel>V√•rdplansnummer</FormLabel>
                    <FormControl>
                      <Input 
                        placeholder="1, 2, 3 etc" 
                        {...field} 
                      />
                    </FormControl>
                    <FormMessage />
                  </FormItem>
                )}
              />

              <FormField
                control={form.control}
                name="clientInitials"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel>Klient initialer</FormLabel>
                    <FormControl>
                      <Input 
                        placeholder="t.ex. A.B." 
                        {...field} 
                      />
                    </FormControl>
                    <FormMessage />
                  </FormItem>
                )}
              />
            </div>

            <div className="grid grid-cols-2 gap-4">
              <FormField
                control={form.control}
                name="receivedDate"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel>Mottagningsdatum</FormLabel>
                    <FormControl>
                      <Input 
                        type="date"
                        {...field} 
                      />
                    </FormControl>
                    <FormMessage />
                  </FormItem>
                )}
              />

              <FormField
                control={form.control}
                name="journalDate"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel>Inskannad i JD (valfritt)</FormLabel>
                    <FormControl>
                      <Input 
                        type="date"
                        {...field} 
                      />
                    </FormControl>
                    <FormMessage />
                  </FormItem>
                )}
              />
            </div>

            <FormField
              control={form.control}
              name="assignedStaffIds"
              render={({ field }) => (
                <FormItem>
                  <FormLabel>Koppla till personal</FormLabel>
                  <FormControl>
                    <Select 
                      onValueChange={(value) => {
                        const currentValues = field.value || [];
                        if (!currentValues.includes(value)) {
                          field.onChange([...currentValues, value]);
                        }
                      }}
                    >
                      <SelectTrigger>
                        <SelectValue placeholder="V√§lj personal att koppla v√•rdplan till" />
                      </SelectTrigger>
                      <SelectContent>
                        {staff.map((staffMember) => (
                          <SelectItem key={staffMember.id} value={staffMember.id}>
                            {staffMember.name} ({staffMember.initials})
                          </SelectItem>
                        ))}
                      </SelectContent>
                    </Select>
                  </FormControl>
                  <div className="flex flex-wrap gap-2 mt-2">
                    {(field.value || []).map((staffId) => {
                      const staffMember = staff.find(s => s.id === staffId);
                      return staffMember ? (
                        <div key={staffId} className="bg-blue-100 text-blue-800 px-2 py-1 rounded text-sm flex items-center gap-2">
                          {staffMember.name}
                          <button
                            type="button"
                            onClick={() => {
                              field.onChange((field.value || []).filter(id => id !== staffId));
                            }}
                            className="text-blue-600 hover:text-blue-800"
                          >
                            √ó
                          </button>
                        </div>
                      ) : null;
                    })}
                  </div>
                  <FormMessage />
                </FormItem>
              )}
            />

            <FormField
              control={form.control}
              name="comment"
              render={({ field }) => (
                <FormItem>
                  <FormLabel>Kommentar (valfritt)</FormLabel>
                  <FormControl>
                    <Textarea 
                      placeholder="Ytterligare kommentarer..." 
                      rows={2}
                      {...field} 
                    />
                  </FormControl>
                  <FormMessage />
                </FormItem>
              )}
            />

            <div className="flex justify-end gap-2 pt-4">
              <Button
                type="button"
                variant="outline"
                onClick={() => setIsOpen(false)}
              >
                Avbryt
              </Button>
              <Button 
                type="submit" 
                className="bg-ungdoms-600 hover:bg-ungdoms-700"
                disabled={createCarePlanMutation.isPending}
              >
                {createCarePlanMutation.isPending ? "Skapar..." : "Skapa v√•rdplan"}
              </Button>
            </div>
          </form>
        </Form>
      </DialogContent>
    </Dialog>
  );
}

client/src/components/care-plan-list.tsx.content:
import * as React from "react";
import { useQuery } from "@tanstack/react-query";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Button } from "@/components/ui/button";
import { Skeleton } from "@/components/ui/skeleton";
import { CarePlanDialog } from "@/components/care-plan-dialog";
import type { CarePlan, Client } from "@shared/schema";
import { Calendar, FileText, Plus, AlertCircle } from "lucide-react";

interface CarePlanListProps {
  clients: Client[];
}

export function CarePlanList({ clients }: CarePlanListProps) {
  const [selectedClient, setSelectedClient] = React.useState<Client | null>(null);

  const { 
    data: carePlans = [], 
    isLoading, 
    isError 
  } = useQuery<CarePlan[]>({
    queryKey: ["/api/care-plans", "all"],
    queryFn: () => fetch("/api/care-plans/all").then(res => res.json()),
  });

  const getStatusBadge = (status: string) => {
    const statusMap = {
      received: { label: "Mottagen", variant: "secondary" as const },
      staff_notified: { label: "Personal meddelad", variant: "default" as const },
      in_progress: { label: "P√•g√•ende", variant: "default" as const },
      completed: { label: "Klar", variant: "default" as const },
    };

    return statusMap[status as keyof typeof statusMap] || { 
      label: status, 
      variant: "secondary" as const 
    };
  };

  const getClientInitials = (clientId: string) => {
    const client = clients.find(c => c.id === clientId);
    return client?.initials || "Ok√§nd klient";
  };

  if (isError) {
    return (
      <Card>
        <CardContent className="flex items-center justify-center py-8">
          <div className="text-center">
            <AlertCircle className="h-8 w-8 text-destructive mx-auto mb-2" />
            <p className="text-sm text-muted-foreground">
              Kunde inte ladda v√•rdplaner. F√∂rs√∂k igen senare.
            </p>
          </div>
        </CardContent>
      </Card>
    );
  }

  return (
    <div className="space-y-4">
      <div className="flex items-center justify-between">
        <h2 className="text-2xl font-bold tracking-tight">V√•rdplaner</h2>
        <Button 
          onClick={() => setSelectedClient(clients[0] || null)}
          disabled={clients.length === 0}
        >
          <Plus className="mr-2 h-4 w-4" />
          Ny v√•rdplan
        </Button>
      </div>

      {isLoading ? (
        <div className="grid gap-4">
          {[1, 2, 3].map((i) => (
            <Card key={i}>
              <CardHeader>
                <Skeleton className="h-5 w-48" />
                <Skeleton className="h-4 w-32" />
              </CardHeader>
              <CardContent>
                <Skeleton className="h-4 w-full mb-2" />
                <Skeleton className="h-4 w-3/4" />
              </CardContent>
            </Card>
          ))}
        </div>
      ) : carePlans.length === 0 ? (
        <Card>
          <CardContent className="flex items-center justify-center py-8">
            <div className="text-center">
              <FileText className="h-8 w-8 text-muted-foreground mx-auto mb-2" />
              <p className="text-sm text-muted-foreground">
                Inga v√•rdplaner finns √§nnu. Skapa en ny f√∂r att komma ig√•ng.
              </p>
            </div>
          </CardContent>
        </Card>
      ) : (
        <div className="grid gap-4">
          {carePlans.map((plan) => {
            const statusInfo = getStatusBadge(plan.status);

            return (
              <Card key={plan.id} className="cursor-pointer hover:shadow-md transition-shadow">
                <CardHeader>
                  <CardTitle className="flex items-center justify-between">
                    <span className="flex items-center">
                      <FileText className="mr-2 h-5 w-5" />
                      {getClientInitials(plan.clientId)}
                    </span>
                    <Badge variant={statusInfo.variant}>
                      {statusInfo.label}
                    </Badge>
                  </CardTitle>
                  {plan.receivedDate && (
                    <div className="flex items-center text-sm text-muted-foreground">
                      <Calendar className="mr-1 h-4 w-4" />
                      Mottagen: {plan.receivedDate}
                    </div>
                  )}
                </CardHeader>
                <CardContent>
                  {plan.goals && (
                    <p className="text-sm text-muted-foreground mb-2">
                      <strong>M√•l:</strong> {plan.goals.substring(0, 100)}
                      {plan.goals.length > 100 && "..."}
                    </p>
                  )}
                  {plan.planContent && (
                    <p className="text-sm text-muted-foreground">
                      <strong>Inneh√•ll:</strong> {plan.planContent.substring(0, 150)}
                      {plan.planContent.length > 150 && "..."}
                    </p>
                  )}
                  {plan.comment && (
                    <p className="text-xs text-muted-foreground mt-2 italic">
                      {plan.comment}
                    </p>
                  )}
                </CardContent>
              </Card>
            );
          })}
        </div>
      )}

      {selectedClient && (
        <CarePlanDialog
          isOpen={true}
          onClose={() => setSelectedClient(null)}
          client={selectedClient}
        />
      )}
    </div>
  );
}

