import { useState } from "react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Textarea } from "@/components/ui/textarea";
import { Checkbox } from "@/components/ui/checkbox";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import { Calendar, CheckCircle, AlertTriangle, Clock, FileText, User } from "lucide-react";
import { useToast } from "@/hooks/use-toast";
import type { Client, CarePlan, ImplementationPlan, WeeklyDocumentation, MonthlyReport, VimsaTime, FollowupPlan } from "@shared/schema";

interface ClientDetailViewProps {
  client: Client;
  staffId: string;
}

export function ClientDetailView({ client, staffId }: ClientDetailViewProps) {
  const { toast } = useToast();
  const queryClient = useQueryClient();

  // Fetch all client-related data
  const { data: carePlan } = useQuery<CarePlan>({
    queryKey: ["/api/care-plans", client.id],
  });

  const { data: implementationPlan } = useQuery<ImplementationPlan>({
    queryKey: ["/api/implementation-plans", client.id],
  });

  const { data: weeklyDocs = [] } = useQuery<WeeklyDocumentation[]>({
    queryKey: ["/api/weekly-documentation", client.id],
  });

  const { data: monthlyReports = [] } = useQuery<MonthlyReport[]>({
    queryKey: ["/api/monthly-reports", client.id],
  });

  const { data: vimsaTimeData = [] } = useQuery<VimsaTime[]>({
    queryKey: ["/api/vimsa-time", client.id],
  });

  const { data: followupPlans = [] } = useQuery<FollowupPlan[]>({
    queryKey: ["/api/followup-plans", client.id],
  });

  // Status helpers
  const getStatusColor = (status: string, isOverdue?: boolean) => {
    if (isOverdue) return "bg-red-100 text-red-800 border-red-200";
    switch (status) {
      case "completed":
      case "sent":
      case "approved":
        return "bg-green-100 text-green-800 border-green-200";
      case "pending":
      case "in_progress":
        return "bg-yellow-100 text-yellow-800 border-yellow-200";
      case "overdue":
      case "rejected":
        return "bg-red-100 text-red-800 border-red-200";
      default:
        return "bg-gray-100 text-gray-800 border-gray-200";
    }
  };

  const getQualityColor = (quality: string) => {
    switch (quality) {
      case "approved":
      case "ok":
        return "bg-green-100 text-green-800 border-green-200";
      case "pending":
      case "s√•d√§r":
        return "bg-yellow-100 text-yellow-800 border-yellow-200";
      case "not_approved":
      case "ej_godk√§nt":
        return "bg-red-100 text-red-800 border-red-200";
      default:
        return "bg-gray-100 text-gray-800 border-gray-200";
    }
  };

  // Check if GFP is overdue (3 weeks from staff notified date)
  const isGfpOverdue = () => {
    if (!carePlan?.staffNotifiedDate || implementationPlan?.status === "completed") return false;
    const notifiedDate = new Date(carePlan.staffNotifiedDate);
    const dueDate = new Date(notifiedDate);
    dueDate.setDate(dueDate.getDate() + 21); // 3 weeks
    return new Date() > dueDate;
  };

  // Generate weeks from week 34 to end of 2025
  const generateWeeks = () => {
    const weeks = [];
    const year = 2025;
    for (let week = 34; week <= 52; week++) {
      weeks.push({ year, week });
    }
    return weeks;
  };

  // Generate months from August to December 2025
  const generateMonths = () => {
    const months = [];
    const year = 2025;
    const monthNames = ["Augusti", "September", "Oktober", "November", "December"];
    for (let month = 8; month <= 12; month++) {
      months.push({ year, month, name: monthNames[month - 8] });
    }
    return months;
  };

  return (
    <div className="space-y-6">
      {/* Client Header */}
      <Card className="border-ungdoms-200">
        <CardHeader>
          <CardTitle className="flex items-center gap-2 text-ungdoms-800">
            <User className="h-5 w-5" />
            Klient: {client.initials}
          </CardTitle>
        </CardHeader>
        <CardContent>
          <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
            <div>
              <p className="text-sm text-muted-foreground">Initialer</p>
              <p className="font-medium">{client.initials}</p>
            </div>
            <div>
              <p className="text-sm text-muted-foreground">Ansvarig personal</p>
              <p className="font-medium">{staffId}</p>
            </div>
            <div>
              <p className="text-sm text-muted-foreground">Status</p>
              <Badge className={getStatusColor(client.status || "active")}>
                {client.status === "active" ? "Aktiv" : "Inaktiv"}
              </Badge>
            </div>
          </div>
        </CardContent>
      </Card>

      <Tabs defaultValue="careplan" className="w-full">
        <TabsList className="grid w-full grid-cols-6">
          <TabsTrigger value="careplan">V√•rdplan</TabsTrigger>
          <TabsTrigger value="gfp">GFP</TabsTrigger>
          <TabsTrigger value="followup">Uppf√∂ljning</TabsTrigger>
          <TabsTrigger value="documentation">Dokumentation</TabsTrigger>
          <TabsTrigger value="monthly">M√•nadsrapporter</TabsTrigger>
          <TabsTrigger value="vimsa">Vimsa Tid</TabsTrigger>
        </TabsList>

        {/* V√•rdplan Tab */}
        <TabsContent value="careplan">
          <Card>
            <CardHeader>
              <CardTitle className="flex items-center gap-2">
                <FileText className="h-5 w-5" />
                V√•rdplan - {client.initials}
              </CardTitle>
            </CardHeader>
            <CardContent>
              <div className="space-y-4">
                <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
                  <div>
                    <label className="text-sm font-medium">Mottagen datum</label>
                    <Input
                      type="date"
                      value={carePlan?.receivedDate ? new Date(carePlan.receivedDate).toISOString().split('T')[0] : ''}
                      className="mt-1"
                    />
                  </div>
                  <div>
                    <label className="text-sm font-medium">Inlagd i journal (digitalt)</label>
                    <Input
                      type="date"
                      value={carePlan?.enteredJournalDate ? new Date(carePlan.enteredJournalDate).toISOString().split('T')[0] : ''}
                      className="mt-1"
                    />
                  </div>
                  <div>
                    <label className="text-sm font-medium">Personal tillsagd</label>
                    <Input
                      type="date"
                      value={carePlan?.staffNotifiedDate ? new Date(carePlan.staffNotifiedDate).toISOString().split('T')[0] : ''}
                      className="mt-1"
                    />
                  </div>
                </div>

                {carePlan?.staffNotifiedDate && (
                  <div className="p-3 bg-blue-50 rounded-lg border border-blue-200">
                    <p className="text-sm text-blue-800">
                      <strong>GFP ska vara inl√§mnad senast:</strong>{" "}
                      {new Date(new Date(carePlan.staffNotifiedDate).getTime() + 21 * 24 * 60 * 60 * 1000)
                        .toLocaleDateString('sv-SE')} (3 veckor fr√•n tills√§gning)
                    </p>
                  </div>
                )}

                <div>
                  <label className="text-sm font-medium">Status</label>
                  <Badge className={`mt-1 ${getStatusColor(carePlan?.status || "received")}`}>
                    {carePlan?.status === "received" && "Mottagen"}
                    {carePlan?.status === "entered_journal" && "Inlagd i journal"}
                    {carePlan?.status === "staff_notified" && "Personal tillsagd"}
                    {carePlan?.status === "gfp_pending" && "V√§ntar p√• GFP"}
                    {carePlan?.status === "completed" && "Slutf√∂rd"}
                  </Badge>
                </div>
              </div>
            </CardContent>
          </Card>
        </TabsContent>

        {/* GFP Tab */}
        <TabsContent value="gfp">
          <Card>
            <CardHeader>
              <CardTitle className="flex items-center gap-2">
                <Calendar className="h-5 w-5" />
                Genomf√∂randeplan (GFP) - {client.initials}
                {isGfpOverdue() && (
                  <Badge className="bg-red-100 text-red-800 border-red-200 ml-2">
                    <AlertTriangle className="h-3 w-3 mr-1" />
                    F√ñRSENAD
                  </Badge>
                )}
              </CardTitle>
            </CardHeader>
            <CardContent>
              <div className="space-y-4">
                <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                  <div>
                    <label className="text-sm font-medium">F√∂rfallodatum</label>
                    <Input
                      type="date"
                      value={implementationPlan?.dueDate ? new Date(implementationPlan.dueDate).toISOString().split('T')[0] : ''}
                      className="mt-1"
                      disabled
                    />
                  </div>
                  <div>
                    <label className="text-sm font-medium">Slutf√∂rd datum</label>
                    <Input
                      type="date"
                      value={implementationPlan?.completedDate ? new Date(implementationPlan.completedDate).toISOString().split('T')[0] : ''}
                      className="mt-1"
                    />
                  </div>
                </div>

                <div>
                  <label className="text-sm font-medium">Skickad datum</label>
                  <Input
                    type="date"
                    value={implementationPlan?.sentDate ? new Date(implementationPlan.sentDate).toISOString().split('T')[0] : ''}
                    className="mt-1"
                  />
                </div>

                <div>
                  <label className="text-sm font-medium">Status</label>
                  <Badge className={`mt-1 ${getStatusColor(implementationPlan?.status || "pending", isGfpOverdue())}`}>
                    {isGfpOverdue() && implementationPlan?.status !== "completed" && "F√ñRSENAD - "}
                    {implementationPlan?.status === "pending" && "V√§ntande"}
                    {implementationPlan?.status === "in_progress" && "P√•g√•ende"}
                    {implementationPlan?.status === "completed" && "Slutf√∂rd"}
                    {implementationPlan?.status === "sent" && "Skickad"}
                  </Badge>
                </div>

                {implementationPlan?.completedDate && (
                  <div className="p-3 bg-green-50 rounded-lg border border-green-200">
                    <p className="text-sm text-green-800 flex items-center gap-2">
                      <CheckCircle className="h-4 w-4" />
                      GFP slutf√∂rd och godk√§nd
                    </p>
                  </div>
                )}
              </div>
            </CardContent>
          </Card>
        </TabsContent>

        {/* Uppf√∂ljningsplan Tab */}
        <TabsContent value="followup">
          <Card>
            <CardHeader>
              <CardTitle className="flex items-center gap-2">
                <Clock className="h-5 w-5" />
                Uppf√∂ljningsplaner - {client.initials}
              </CardTitle>
            </CardHeader>
            <CardContent>
              <div className="space-y-4">
                {followupPlans.length > 0 ? (
                  followupPlans.map((plan, index) => (
                    <div key={plan.id} className="p-4 border border-ungdoms-200 rounded-lg">
                      <div className="flex items-center justify-between mb-2">
                        <h4 className="font-medium">Uppf√∂ljning {index + 1}</h4>
                        <Badge className={getStatusColor(plan.status)}>
                          {plan.status === "scheduled" && "Planerad"}
                          {plan.status === "completed" && "Slutf√∂rd"}
                          {plan.status === "cancelled" && "Inst√§lld"}
                        </Badge>
                      </div>
                      <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                        <div>
                          <p className="text-sm text-muted-foreground">Planerat datum</p>
                          <p>{new Date(plan.followupDate).toLocaleDateString('sv-SE')}</p>
                        </div>
                        {plan.completedDate && (
                          <div>
                            <p className="text-sm text-muted-foreground">Slutf√∂rd datum</p>
                            <p>{new Date(plan.completedDate).toLocaleDateString('sv-SE')}</p>
                          </div>
                        )}
                      </div>
                      {plan.notes && (
                        <div className="mt-2">
                          <p className="text-sm text-muted-foreground">Anteckningar</p>
                          <p className="text-sm">{plan.notes}</p>
                        </div>
                      )}
                    </div>
                  ))
                ) : (
                  <p className="text-muted-foreground text-center py-8">Inga uppf√∂ljningsplaner registrerade</p>
                )}
              </div>
            </CardContent>
          </Card>
        </TabsContent>

        {/* Dokumentation Tab */}
        <TabsContent value="documentation">
          <Card>
            <CardHeader>
              <CardTitle className="flex items-center gap-2">
                <FileText className="h-5 w-5" />
                Veckodokumentation 2025 - {client.initials}
              </CardTitle>
            </CardHeader>
            <CardContent>
              <div className="space-y-4">
                <p className="text-sm text-muted-foreground">
                  Vecka 34 - 52, 2025. Gr√∂n = Godk√§nd kvalitet, Ingen f√§rg = Dokumenterad, R√∂d = Saknas
                </p>
                <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
                  {generateWeeks().map(({ year, week }) => {
                    const doc = weeklyDocs.find(d => d.year === year && d.week === week);
                    return (
                      <div key={`${year}-${week}`} className="p-3 border border-gray-200 rounded-lg">
                        <div className="flex items-center justify-between mb-2">
                          <span className="font-medium">Vecka {week}</span>
                          <Badge className={
                            !doc ? "bg-red-100 text-red-800 border-red-200" :
                            doc.qualityAssessment === "approved" ? "bg-green-100 text-green-800 border-green-200" :
                            "bg-gray-100 text-gray-800 border-gray-200"
                          }>
                            {!doc ? "Saknas" : doc.qualityAssessment === "approved" ? "Godk√§nd" : "Dokumenterad"}
                          </Badge>
                        </div>

                        <div className="space-y-2">
                          <div className="flex items-center gap-2">
                            <Checkbox checked={!!doc} />
                            <span className="text-sm">Dokumenterad</span>
                          </div>
                          <div className="flex items-center gap-2">
                            <Checkbox checked={doc?.qualityAssessment === "approved"} />
                            <span className="text-sm">Kvalitet godk√§nd</span>
                          </div>
                        </div>

                        {doc?.comments && (
                          <div className="mt-2">
                            <p className="text-xs text-muted-foreground">Kommentar:</p>
                            <p className="text-xs">{doc.comments}</p>
                          </div>
                        )}
                      </div>
                    );
                  })}
                </div>
              </div>
            </CardContent>
          </Card>
        </TabsContent>

        {/* M√•nadsrapporter Tab */}
        <TabsContent value="monthly">
          <Card>
            <CardHeader>
              <CardTitle className="flex items-center gap-2">
                <Calendar className="h-5 w-5" />
                M√•nadsrapporter 2025 - {client.initials}
              </CardTitle>
            </CardHeader>
            <CardContent>
              <div className="space-y-4">
                {generateMonths().map(({ year, month, name }) => {
                  const report = monthlyReports.find(r => r.year === year && r.month === month);
                  return (
                    <div key={`${year}-${month}`} className="p-4 border border-ungdoms-200 rounded-lg">
                      <div className="flex items-center justify-between mb-3">
                        <h4 className="font-medium">{name} {year}</h4>
                        <div className="flex gap-2">
                          <Badge className={getStatusColor(report?.status || "not_submitted")}>
                            {report?.status === "submitted" && "Skickad"}
                            {report?.status === "late" && "F√∂rsenad"}
                            {!report && "Ej inl√§mnad"}
                          </Badge>
                          {report?.quality && (
                            <Badge className={getQualityColor(report.quality)}>
                              {report.quality === "approved" && "Godk√§nd"}
                              {report.quality === "pending" && "S√•d√§r"}
                              {report.quality === "not_approved" && "Ej godk√§nd"}
                            </Badge>
                          )}
                        </div>
                      </div>

                      <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
                        <div className="flex items-center gap-2">
                          <Checkbox checked={!!report} />
                          <span className="text-sm">Skickad</span>
                        </div>
                        <div>
                          <Select value={report?.status || "not_submitted"}>
                            <SelectTrigger className="h-8">
                              <SelectValue placeholder="Status" />
                            </SelectTrigger>
                            <SelectContent>
                              <SelectItem value="not_submitted">Ej inl√§mnad</SelectItem>
                              <SelectItem value="submitted">I tid</SelectItem>
                              <SelectItem value="late">F√∂rsenad</SelectItem>
                            </SelectContent>
                          </Select>
                        </div>
                        <div>
                          <Select value={report?.quality || "pending"}>
                            <SelectTrigger className="h-8">
                              <SelectValue placeholder="Kvalitet" />
                            </SelectTrigger>
                            <SelectContent>
                              <SelectItem value="pending">S√•d√§r</SelectItem>
                              <SelectItem value="approved">OK</SelectItem>
                              <SelectItem value="not_approved">Ej godk√§nt</SelectItem>
                            </SelectContent>
                          </Select>
                        </div>
                      </div>

                      {report?.submissionDate && (
                        <div className="mt-2">
                          <p className="text-sm text-muted-foreground">
                            Inl√§mnad: {new Date(report.submissionDate).toLocaleDateString('sv-SE')}
                          </p>
                        </div>
                      )}

                      <div className="mt-3">
                        <Textarea
                          placeholder="Kommentarer..."
                          value={report?.comment || ""}
                          className="h-20"
                        />
                      </div>
                    </div>
                  );
                })}
              </div>
            </CardContent>
          </Card>
        </TabsContent>

        {/* Vimsa Tid Tab */}
        <TabsContent value="vimsa">
          <Card>
            <CardHeader>
              <CardTitle className="flex items-center gap-2">
                <Clock className="h-5 w-5" />
                Vimsa Tid - {client.initials}
              </CardTitle>
            </CardHeader>
            <CardContent>
              <div className="space-y-4">
                <p className="text-sm text-muted-foreground">
                  Gr√∂n = St√§mmer med dokumentation, R√∂d = St√§mmer inte
                </p>

                {generateWeeks().map(({ year, week }) => {
                  const timeData = vimsaTimeData.find(v => v.year === year && v.week === week);
                  return (
                    <div key={`${year}-${week}`} className="p-4 border border-ungdoms-200 rounded-lg">
                      <div className="flex items-center justify-between mb-3">
                        <h4 className="font-medium">Vecka {week}, {year}</h4>
                        <Badge className={
                          timeData?.matchesDocumentation ? 
                            "bg-green-100 text-green-800 border-green-200" : 
                            "bg-red-100 text-red-800 border-red-200"
                        }>
                          {timeData?.matchesDocumentation ? "St√§mmer" : "St√§mmer inte"}
                        </Badge>
                      </div>

                      <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
                        <div>
                          <label className="text-sm font-medium">Timmar arbetade</label>
                          <Input
                            type="number"
                            value={timeData?.hoursWorked || 0}
                            className="mt-1"
                          />
                        </div>
                        <div>
                          <label className="text-sm font-medium">St√§mmer med dokumentation</label>
                          <Select value={timeData?.matchesDocumentation ? "yes" : "no"}>
                            <SelectTrigger className="mt-1">
                              <SelectValue />
                            </SelectTrigger>
                            <SelectContent>
                              <SelectItem value="yes">Ja (Gr√∂n)</SelectItem>
                              <SelectItem value="no">Nej (R√∂d)</SelectItem>
                            </SelectContent>
                          </Select>
                        </div>
                        <div>
                          <label className="text-sm font-medium">Status</label>
                          <Select value={timeData?.status || "pending"}>
                            <SelectTrigger className="mt-1">
                              <SelectValue />
                            </SelectTrigger>
                            <SelectContent>
                              <SelectItem value="pending">V√§ntande</SelectItem>
                              <SelectItem value="approved">Godk√§nd</SelectItem>
                              <SelectItem value="rejected">Avvisad</SelectItem>
                            </SelectContent>
                          </Select>
                        </div>
                      </div>

                      <div className="mt-3">
                        <label className="text-sm font-medium">Kommentarer</label>
                        <Textarea
                          placeholder="Kommentarer om Vimsa tid..."
                          value={timeData?.comment || ""}
                          className="mt-1 h-20"
                        />
                      </div>
                    </div>
                  );
                })}
              </div>
            </CardContent>
          </Card>
        </TabsContent>
      </Tabs>
    </div>
  );
}

