import { useState } from "react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import { Input } from "@/components/ui/input";
import { Textarea } from "@/components/ui/textarea";
import { Label } from "@/components/ui/label";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Form, FormControl, FormField, FormItem, FormLabel, FormMessage } from "@/components/ui/form";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { 
  Heart, 
  Clipboard, 
  FileText, 
  Calendar, 
  Clock,
  Plus,
  Save,
  CheckCircle2
} from "lucide-react";
import { useToast } from "@/hooks/use-toast";
import { apiRequest } from "@/lib/queryClient";
import { 
  insertCarePlanSchema,
  insertImplementationPlanSchema,
  insertWeeklyDocumentationSchema,
  insertMonthlyReportSchema,
  insertVimsaTimeSchema,
  insertClientSchema,
  type InsertCarePlan,
  type InsertImplementationPlan,
  type InsertWeeklyDocumentation,
  type InsertMonthlyReport,
  type InsertVimsaTime,
  type InsertClient,
  type Staff 
} from "@shared/schema";

interface ClientWorkflowProps {
  staff: Staff;
}

export function ClientWorkflow({ staff }: ClientWorkflowProps) {
  const [activeTab, setActiveTab] = useState("careplan");
  const [selectedClientId, setSelectedClientId] = useState<string>("");
  const { toast } = useToast();
  const queryClient = useQueryClient();

  // H√§mta klienter f√∂r denna anst√§lld
  const { data: clients = [], isLoading: clientsLoading } = useQuery<Client[]>({
    queryKey: ["/api/staff", staff.id, "clients"],
  });

  // Current week/month helpers
  const getCurrentWeek = () => {
    const now = new Date();
    const year = now.getFullYear();
    const startOfYear = new Date(year, 0, 1);
    const days = Math.floor((now.getTime() - startOfYear.getTime()) / (24 * 60 * 60 * 1000));
    const week = Math.ceil((days + startOfYear.getDay() + 1) / 7);
    return { year, week };
  };

  const getCurrentMonth = () => {
    const now = new Date();
    return { year: now.getFullYear(), month: now.getMonth() + 1 };
  };

  const { year: currentYear, week: currentWeek } = getCurrentWeek();
  const { month: currentMonth } = getCurrentMonth();

  // Formul√§r f√∂r att skapa ny klient
  const newClientForm = useForm<InsertClient>({
    resolver: zodResolver(insertClientSchema),
    defaultValues: {
      staffId: staff.id,
      initials: "",
      name: "",
    },
  });

  // V√•rdplan formul√§r
  const carePlanForm = useForm<InsertCarePlan>({
    resolver: zodResolver(insertCarePlanSchema),
    defaultValues: {
      clientId: selectedClientId,
      planContent: "",
      goals: "",
      interventions: "",
      evaluationCriteria: "",
      isActive: true,
    },
  });

  // Genomf√∂randeplan formul√§r
  const implementationForm = useForm<InsertImplementationPlan>({
    resolver: zodResolver(insertImplementationPlanSchema),
    defaultValues: {
      clientId: selectedClientId,
      planContent: "",
      goals: "",
      activities: "",
      followUpSchedule: "",
      isActive: true,
    },
  });

  // Veckodokumentation formul√§r
  const weeklyDocForm = useForm<InsertWeeklyDocumentation>({
    resolver: zodResolver(insertWeeklyDocumentationSchema),
    defaultValues: {
      clientId: selectedClientId,
      year: currentYear,
      week: currentWeek,
      documentation: "",
      status: "not_done",
      qualityAssessment: "pending",
      comments: "",
    },
  });

  // M√•nadsrapport formul√§r
  const monthlyReportForm = useForm<InsertMonthlyReport>({
    resolver: zodResolver(insertMonthlyReportSchema),
    defaultValues: {
      clientId: selectedClientId,
      year: currentYear,
      month: currentMonth,
      reportContent: "",
      status: "not_submitted",
      quality: "pending",
      qualityScore: 0,
    },
  });

  // Vimsa Tid formul√§r
  const vimsaTimeForm = useForm<InsertVimsaTime>({
    resolver: zodResolver(insertVimsaTimeSchema),
    defaultValues: {
      clientId: selectedClientId,
      year: currentYear,
      week: currentWeek,
      hoursWorked: 0,
      status: "pending",
      activities: "",
      notes: "",
    },
  });

  // Mutationer
  const createClientMutation = useMutation({
    mutationFn: async (data: InsertClient) => {
      const response = await apiRequest("POST", "/api/clients", data);
      return response.json();
    },
    onSuccess: (newClient) => {
      toast({ title: "Framg√•ng", description: "Klient skapad!" });
      newClientForm.reset();
      setSelectedClientId(newClient.id);
      queryClient.invalidateQueries({ queryKey: ["/api/staff", staff.id, "clients"] });
    },
  });

  const createCarePlanMutation = useMutation({
    mutationFn: async (data: InsertCarePlan) => {
      const response = await apiRequest("POST", "/api/care-plans", data);
      return response.json();
    },
    onSuccess: () => {
      toast({ title: "Framg√•ng", description: "V√•rdplan sparad!" });
      setActiveTab("implementation");
    },
  });

  const createImplementationMutation = useMutation({
    mutationFn: async (data: InsertImplementationPlan) => {
      const response = await apiRequest("POST", "/api/implementation-plans", data);
      return response.json();
    },
    onSuccess: () => {
      toast({ title: "Framg√•ng", description: "Genomf√∂randeplan sparad!" });
      setActiveTab("documentation");
    },
  });

  const createWeeklyDocMutation = useMutation({
    mutationFn: async (data: InsertWeeklyDocumentation) => {
      const response = await apiRequest("POST", "/api/weekly-documentation", data);
      return response.json();
    },
    onSuccess: () => {
      toast({ title: "Framg√•ng", description: "Veckodokumentation sparad!" });
      setActiveTab("monthly");
    },
  });

  const createMonthlyReportMutation = useMutation({
    mutationFn: async (data: InsertMonthlyReport) => {
      const response = await apiRequest("POST", "/api/monthly-reports", data);
      return response.json();
    },
    onSuccess: () => {
      toast({ title: "Framg√•ng", description: "M√•nadsrapport sparad!" });
      setActiveTab("vimsa");
    },
  });

  const createVimsaTimeMutation = useMutation({
    mutationFn: async (data: InsertVimsaTime) => {
      const response = await apiRequest("POST", "/api/vimsa-time", data);
      return response.json();
    },
    onSuccess: () => {
      toast({ title: "Framg√•ng", description: "Vimsa Tid sparad! Klient komplett!" });
    },
  });

  if (clientsLoading) {
    return (
      <div className="text-center py-8">
        <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-ungdoms-500 mx-auto mb-4"></div>
        <p className="text-muted-foreground">Laddar klientdata...</p>
      </div>
    );
  }

  return (
    <div className="space-y-6">
      {/* Klientv√§ljare */}
      <Card className="border-ungdoms-600">
        <CardHeader>
          <CardTitle className="flex items-center">
            <Heart className="mr-2 h-5 w-5 text-ungdoms-400" />
            Klient f√∂r {staff.name}
          </CardTitle>
        </CardHeader>
        <CardContent>
          <div className="flex gap-4 items-end">
            <div className="flex-1">
              <Label htmlFor="client-select">V√§lj befintlig klient</Label>
              <Select value={selectedClientId || ""} onValueChange={setSelectedClientId}>
                <SelectTrigger>
                  <SelectValue placeholder="V√§lj klient..." />
                </SelectTrigger>
                <SelectContent>
                  {clients.map((client) => (
                    <SelectItem key={client.id} value={client.id}>
                      {client.initials} {client.name && `(${client.name})`}
                    </SelectItem>
                  ))}
                </SelectContent>
              </Select>
            </div>

            <div className="text-muted-foreground text-sm">ELLER</div>

            <div className="flex gap-2">
              <Input
                placeholder="Initialer (t.ex. AB)"
                value={newClientForm.watch("initials")}
                onChange={(e) => newClientForm.setValue("initials", e.target.value.toUpperCase())}
                className="w-20"
                maxLength={3}
              />
              <Input
                placeholder="Namn (valfritt)"
                value={newClientForm.watch("name")}
                onChange={(e) => newClientForm.setValue("name", e.target.value)}
                className="w-40"
              />
              <Button
                onClick={() => createClientMutation.mutate(newClientForm.getValues())}
                disabled={!newClientForm.watch("initials") || createClientMutation.isPending}
                className="bg-ungdoms-500 hover:bg-ungdoms-600"
              >
                <Plus className="h-4 w-4 mr-1" />
                Skapa
              </Button>
            </div>
          </div>
        </CardContent>
      </Card>

      {/* Huvudfl√∂de med tabbar */}
      {selectedClientId && (
        <Card className="border-ungdoms-600">
          <CardHeader>
            <CardTitle>
              Klientfl√∂de f√∂r {clients.find((c) => c.id === selectedClientId)?.initials}
            </CardTitle>
          </CardHeader>
          <CardContent>
            <Tabs value={activeTab} onValueChange={setActiveTab} className="w-full">
              <TabsList className="grid w-full grid-cols-5">
                <TabsTrigger value="careplan" className="flex items-center gap-1">
                  <Heart className="h-4 w-4" />
                  1. V√•rdplan
                </TabsTrigger>
                <TabsTrigger value="implementation" className="flex items-center gap-1">
                  <Clipboard className="h-4 w-4" />
                  2. Genomf√∂randeplan
                </TabsTrigger>
                <TabsTrigger value="documentation" className="flex items-center gap-1">
                  <FileText className="h-4 w-4" />
                  3. Dokumentation
                </TabsTrigger>
                <TabsTrigger value="monthly" className="flex items-center gap-1">
                  <Calendar className="h-4 w-4" />
                  4. M√•nadsrapport
                </TabsTrigger>
                <TabsTrigger value="vimsa" className="flex items-center gap-1">
                  <Clock className="h-4 w-4" />
                  5. Vimsa Tid
                </TabsTrigger>
              </TabsList>

              {/* V√ÖRDPLAN */}
              <TabsContent value="careplan" className="space-y-4">
                <Card>
                  <CardHeader>
                    <CardTitle className="flex items-center">
                      <Heart className="mr-2 h-5 w-5 text-ungdoms-400" />
                      Steg 1: V√•rdplan
                    </CardTitle>
                  </CardHeader>
                  <CardContent>
                    <Form {...carePlanForm}>
                      <form onSubmit={carePlanForm.handleSubmit((data) => {
                        createCarePlanMutation.mutate({ ...data, clientId: selectedClientId });
                      })} className="space-y-4">
                        <FormField
                          control={carePlanForm.control}
                          name="planContent"
                          render={({ field }) => (
                            <FormItem>
                              <FormLabel>V√•rdplanens inneh√•ll</FormLabel>
                              <FormControl>
                                <Textarea 
                                  placeholder="Beskrivning av v√•rdplanen..." 
                                  rows={6} 
                                  {...field}
                                  value={field.value || ""}
                                />
                              </FormControl>
                              <FormMessage />
                            </FormItem>
                          )}
                        />

                        <div className="grid grid-cols-2 gap-4">
                          <FormField
                            control={carePlanForm.control}
                            name="goals"
                            render={({ field }) => (
                              <FormItem>
                                <FormLabel>M√•l</FormLabel>
                                <FormControl>
                                  <Textarea 
                                    placeholder="V√•rdm√•l och delm√•l..." 
                                    rows={4} 
                                    {...field} 
                                  />
                                </FormControl>
                                <FormMessage />
                              </FormItem>
                            )}
                          />

                          <FormField
                            control={carePlanForm.control}
                            name="interventions"
                            render={({ field }) => (
                              <FormItem>
                                <FormLabel>Interventioner</FormLabel>
                                <FormControl>
                                  <Textarea 
                                    placeholder="Planerade interventioner..." 
                                    rows={4} 
                                    {...field} 
                                  />
                                </FormControl>
                                <FormMessage />
                              </FormItem>
                            )}
                          />
                        </div>

                        <FormField
                          control={carePlanForm.control}
                          name="evaluationCriteria"
                          render={({ field }) => (
                            <FormItem>
                              <FormLabel>Utv√§rderingskriterier</FormLabel>
                              <FormControl>
                                <Textarea 
                                  placeholder="Kriterier f√∂r utv√§rdering..." 
                                  rows={3} 
                                  {...field} 
                                />
                              </FormControl>
                              <FormMessage />
                            </FormItem>
                          )}
                        />

                        <Button 
                          type="submit" 
                          disabled={createCarePlanMutation.isPending}
                          className="bg-ungdoms-500 hover:bg-ungdoms-600"
                        >
                          <CheckCircle2 className="mr-2 h-4 w-4" />
                          Spara och g√• till Genomf√∂randeplan
                        </Button>
                      </form>
                    </Form>
                  </CardContent>
                </Card>
              </TabsContent>

              {/* GENOMF√ñRANDEPLAN */}
              <TabsContent value="implementation" className="space-y-4">
                <Card>
                  <CardHeader>
                    <CardTitle className="flex items-center">
                      <Clipboard className="mr-2 h-5 w-5 text-ungdoms-400" />
                      Steg 2: Genomf√∂randeplan (GFP)
                    </CardTitle>
                  </CardHeader>
                  <CardContent>
                    <Form {...implementationForm}>
                      <form onSubmit={implementationForm.handleSubmit((data) => {
                        createImplementationMutation.mutate({ ...data, clientId: selectedClientId });
                      })} className="space-y-4">
                        <div className="grid grid-cols-2 gap-4">
                          <FormField
                            control={implementationForm.control}
                            name="startDate"
                            render={({ field }) => (
                              <FormItem>
                                <FormLabel>Startdatum</FormLabel>
                                <FormControl>
                                  <Input 
                                    type="date" 
                                    {...field} 
                                    value={field.value ? new Date(field.value).toISOString().split('T')[0] : ''}
                                    onChange={(e) => field.onChange(e.target.value ? new Date(e.target.value) : null)}
                                  />
                                </FormControl>
                                <FormMessage />
                              </FormItem>
                            )}
                          />

                          <FormField
                            control={implementationForm.control}
                            name="endDate"
                            render={({ field }) => (
                              <FormItem>
                                <FormLabel>Slutdatum (3 m√•nader)</FormLabel>
                                <FormControl>
                                  <Input 
                                    type="date" 
                                    {...field} 
                                    value={field.value ? new Date(field.value).toISOString().split('T')[0] : ''}
                                    onChange={(e) => field.onChange(e.target.value ? new Date(e.target.value) : null)}
                                  />
                                </FormControl>
                                <FormMessage />
                              </FormItem>
                            )}
                          />
                        </div>

                        <FormField
                          control={implementationForm.control}
                          name="planContent"
                          render={({ field }) => (
                            <FormItem>
                              <FormLabel>Planinneh√•ll</FormLabel>
                              <FormControl>
                                <Textarea 
                                  placeholder="Genomf√∂randeplanens inneh√•ll..." 
                                  rows={6} 
                                  {...field} 
                                />
                              </FormControl>
                              <FormMessage />
                            </FormItem>
                          )}
                        />

                        <div className="grid grid-cols-2 gap-4">
                          <FormField
                            control={implementationForm.control}
                            name="goals"
                            render={({ field }) => (
                              <FormItem>
                                <FormLabel>M√•l</FormLabel>
                                <FormControl>
                                  <Textarea 
                                    placeholder="Specifika m√•l f√∂r genomf√∂rande..." 
                                    rows={4} 
                                    {...field} 
                                  />
                                </FormControl>
                                <FormMessage />
                              </FormItem>
                            )}
                          />

                          <FormField
                            control={implementationForm.control}
                            name="activities"
                            render={({ field }) => (
                              <FormItem>
                                <FormLabel>Aktiviteter</FormLabel>
                                <FormControl>
                                  <Textarea 
                                    placeholder="Planerade aktiviteter..." 
                                    rows={4} 
                                    {...field} 
                                  />
                                </FormControl>
                                <FormMessage />
                              </FormItem>
                            )}
                          />
                        </div>

                        <Button 
                          type="submit" 
                          disabled={createImplementationMutation.isPending}
                          className="bg-ungdoms-500 hover:bg-ungdoms-600"
                        >
                          <CheckCircle2 className="mr-2 h-4 w-4" />
                          Spara och g√• till Dokumentation
                        </Button>
                      </form>
                    </Form>
                  </CardContent>
                </Card>
              </TabsContent>

              {/* VECKODOKUMENTATION */}
              <TabsContent value="documentation" className="space-y-4">
                <Card>
                  <CardHeader>
                    <CardTitle className="flex items-center">
                      <FileText className="mr-2 h-5 w-5 text-ungdoms-400" />
                      Steg 3: Veckodokumentation - Vecka {currentWeek}, {currentYear}
                    </CardTitle>
                  </CardHeader>
                  <CardContent>
                    <Form {...weeklyDocForm}>
                      <form onSubmit={weeklyDocForm.handleSubmit((data) => {
                        createWeeklyDocMutation.mutate({ ...data, clientId: selectedClientId });
                      })} className="space-y-4">
                        <div className="grid grid-cols-2 gap-4">
                          <FormField
                            control={weeklyDocForm.control}
                            name="status"
                            render={({ field }) => (
                              <FormItem>
                                <FormLabel>Dokumentation Status</FormLabel>
                                <Select onValueChange={field.onChange} defaultValue={field.value}>
                                  <FormControl>
                                    <SelectTrigger>
                                      <SelectValue />
                                    </SelectTrigger>
                                  </FormControl>
                                  <SelectContent>
                                    <SelectItem value="done">Klar</SelectItem>
                                    <SelectItem value="reminded">P√•mind</SelectItem>
                                    <SelectItem value="not_done">Ej klar</SelectItem>
                                  </SelectContent>
                                </Select>
                                <FormMessage />
                              </FormItem>
                            )}
                          />

                          <FormField
                            control={weeklyDocForm.control}
                            name="qualityAssessment"
                            render={({ field }) => (
                              <FormItem>
                                <FormLabel>Kvalitetsbed√∂mning</FormLabel>
                                <Select onValueChange={field.onChange} defaultValue={field.value}>
                                  <FormControl>
                                    <SelectTrigger>
                                      <SelectValue />
                                    </SelectTrigger>
                                  </FormControl>
                                  <SelectContent>
                                    <SelectItem value="approved">Godk√§nd</SelectItem>
                                    <SelectItem value="not_approved">Ej godk√§nd</SelectItem>
                                    <SelectItem value="pending">V√§ntar</SelectItem>
                                  </SelectContent>
                                </Select>
                                <FormMessage />
                              </FormItem>
                            )}
                          />
                        </div>

                        <FormField
                          control={weeklyDocForm.control}
                          name="documentation"
                          render={({ field }) => (
                            <FormItem>
                              <FormLabel>Dokumentation</FormLabel>
                              <FormControl>
                                <Textarea 
                                  placeholder="Veckans dokumentation..." 
                                  rows={6} 
                                  {...field} 
                                />
                              </FormControl>
                              <FormMessage />
                            </FormItem>
                          )}
                        />

                        <FormField
                          control={weeklyDocForm.control}
                          name="comments"
                          render={({ field }) => (
                            <FormItem>
                              <FormLabel>Kommentarer</FormLabel>
                              <FormControl>
                                <Textarea 
                                  placeholder="√ñvriga kommentarer..." 
                                  rows={3} 
                                  {...field} 
                                />
                              </FormControl>
                              <FormMessage />
                            </FormItem>
                          )}
                        />

                        <Button 
                          type="submit" 
                          disabled={createWeeklyDocMutation.isPending}
                          className="bg-ungdoms-500 hover:bg-ungdoms-600"
                        >
                          <CheckCircle2 className="mr-2 h-4 w-4" />
                          Spara och g√• till M√•nadsrapport
                        </Button>
                      </form>
                    </Form>
                  </CardContent>
                </Card>
              </TabsContent>

              {/* M√ÖNADSRAPPORT */}
              <TabsContent value="monthly" className="space-y-4">
                <Card>
                  <CardHeader>
                    <CardTitle className="flex items-center">
                      <Calendar className="mr-2 h-5 w-5 text-ungdoms-400" />
                      Steg 4: M√•nadsrapport - {currentMonth}/{currentYear}
                    </CardTitle>
                  </CardHeader>
                  <CardContent>
                    <Form {...monthlyReportForm}>
                      <form onSubmit={monthlyReportForm.handleSubmit((data) => {
                        createMonthlyReportMutation.mutate({ ...data, clientId: selectedClientId });
                      })} className="space-y-4">
                        <div className="grid grid-cols-2 gap-4">
                          <FormField
                            control={monthlyReportForm.control}
                            name="status"
                            render={({ field }) => (
                              <FormItem>
                                <FormLabel>Rapport Status</FormLabel>
                                <Select onValueChange={field.onChange} defaultValue={field.value}>
                                  <FormControl>
                                    <SelectTrigger>
                                      <SelectValue />
                                    </SelectTrigger>
                                  </FormControl>
                                  <SelectContent>
                                    <SelectItem value="submitted">Inl√§mnad</SelectItem>
                                    <SelectItem value="late">F√∂rsenad</SelectItem>
                                    <SelectItem value="not_submitted">Ej inl√§mnad</SelectItem>
                                  </SelectContent>
                                </Select>
                                <FormMessage />
                              </FormItem>
                            )}
                          />

                          <FormField
                            control={monthlyReportForm.control}
                            name="qualityScore"
                            render={({ field }) => (
                              <FormItem>
                                <FormLabel>Kvalitetspo√§ng (1-10)</FormLabel>
                                <FormControl>
                                  <Input 
                                    type="number" 
                                    min="1" 
                                    max="10" 
                                    {...field}
                                    onChange={(e) => field.onChange(parseInt(e.target.value))}
                                  />
                                </FormControl>
                                <FormMessage />
                              </FormItem>
                            )}
                          />
                        </div>

                        <FormField
                          control={monthlyReportForm.control}
                          name="reportContent"
                          render={({ field }) => (
                            <FormItem>
                              <FormLabel>Rapportinneh√•ll</FormLabel>
                              <FormControl>
                                <Textarea 
                                  placeholder="M√•nadsrapportens inneh√•ll..." 
                                  rows={8} 
                                  {...field} 
                                />
                              </FormControl>
                              <FormMessage />
                            </FormItem>
                          )}
                        />

                        <Button 
                          type="submit" 
                          disabled={createMonthlyReportMutation.isPending}
                          className="bg-ungdoms-500 hover:bg-ungdoms-600"
                        >
                          <CheckCircle2 className="mr-2 h-4 w-4" />
                          Spara och g√• till Vimsa Tid
                        </Button>
                      </form>
                    </Form>
                  </CardContent>
                </Card>
              </TabsContent>

              {/* VIMSA TID */}
              <TabsContent value="vimsa" className="space-y-4">
                <Card>
                  <CardHeader>
                    <CardTitle className="flex items-center">
                      <Clock className="mr-2 h-5 w-5 text-ungdoms-400" />
                      Steg 5: Vimsa Tid - Vecka {currentWeek}, {currentYear}
                    </CardTitle>
                  </CardHeader>
                  <CardContent>
                    <Form {...vimsaTimeForm}>
                      <form onSubmit={vimsaTimeForm.handleSubmit((data) => {
                        createVimsaTimeMutation.mutate({ ...data, clientId: selectedClientId });
                      })} className="space-y-4">
                        <div className="grid grid-cols-2 gap-4">
                          <FormField
                            control={vimsaTimeForm.control}
                            name="hoursWorked"
