                            )}
                          />

                          <FormField
                            control={vimsaTimeForm.control}
                            name="status"
                            render={({ field }) => (
                              <FormItem>
                                <FormLabel>Status</FormLabel>
                                <Select onValueChange={field.onChange} defaultValue={field.value}>
                                  <FormControl>
                                    <SelectTrigger>
                                      <SelectValue />
                                    </SelectTrigger>
                                  </FormControl>
                                  <SelectContent>
                                    <SelectItem value="accepted">Accepterad</SelectItem>
                                    <SelectItem value="rejected">Avvisad</SelectItem>
                                    <SelectItem value="pending">V√§ntar</SelectItem>
                                  </SelectContent>
                                </Select>
                                <FormMessage />
                              </FormItem>
                            )}
                          />
                        </div>

                        <FormField
                          control={vimsaTimeForm.control}
                          name="activities"
                          render={({ field }) => (
                            <FormItem>
                              <FormLabel>Aktiviteter</FormLabel>
                              <FormControl>
                                <Textarea 
                                  placeholder="Beskrivning av utf√∂rda aktiviteter..." 
                                  rows={5} 
                                  {...field} 
                                />
                              </FormControl>
                              <FormMessage />
                            </FormItem>
                          )}
                        />

                        <FormField
                          control={vimsaTimeForm.control}
                          name="notes"
                          render={({ field }) => (
                            <FormItem>
                              <FormLabel>Anteckningar</FormLabel>
                              <FormControl>
                                <Textarea 
                                  placeholder="√ñvriga anteckningar..." 
                                  rows={3} 
                                  {...field} 
                                />
                              </FormControl>
                              <FormMessage />
                            </FormItem>
                          )}
                        />

                        <Button 
                          type="submit" 
                          disabled={createVimsaTimeMutation.isPending}
                          className="bg-green-600 hover:bg-green-700"
                        >
                          <CheckCircle2 className="mr-2 h-4 w-4" />
                          Slutf√∂r klientfl√∂det!
                        </Button>
                      </form>
                    </Form>
                  </CardContent>
                </Card>
              </TabsContent>
            </Tabs>
          </CardContent>
        </Card>
      )}
    </div>
  );
}

