client/src/components/login-form.tsx.content:
import { useState } from "react";
import { useMutation } from "@tanstack/react-query";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Form, FormControl, FormField, FormItem, FormLabel, FormMessage } from "@/components/ui/form";
import { Input } from "@/components/ui/input";
import { Button } from "@/components/ui/button";
import { Alert, AlertDescription } from "@/components/ui/alert";
import { UngdomsLogo } from "@/components/ungdoms-logo";
import { LoginData, loginSchema } from "@shared/schema";
import { apiRequest } from "@/lib/queryClient";
import { useToast } from "@/hooks/use-toast";
import { Shield, Lock, User } from "lucide-react";

interface LoginFormProps {
  onLoginSuccess: (user: any) => void;
}

export function LoginForm({ onLoginSuccess }: LoginFormProps) {
  const [error, setError] = useState<string>("");
  const { toast } = useToast();

  const form = useForm<LoginData>({
    resolver: zodResolver(loginSchema),
    defaultValues: {
      username: "",
      password: "",
    },
  });

  const loginMutation = useMutation({
    mutationFn: async (data: LoginData) => {
      setError("");
      const response = await apiRequest("POST", "/api/auth/login", data);
      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.message || "Inloggning misslyckades");
      }
      return response.json();
    },
    onSuccess: (userData) => {
      toast({
        title: "V√§lkommen!",
        description: `Inloggad som ${userData.user.username}`,
      });
      onLoginSuccess(userData.user);
    },
    onError: (error: Error) => {
      setError(error.message);
    },
  });

  return (
    <div className="min-h-screen bg-gradient-to-b from-ungdoms-50 to-white flex items-center justify-center p-4">
      <Card className="w-full max-w-md border-ungdoms-200 shadow-lg">
        <CardHeader className="text-center pb-4">
          <div className="flex justify-center mb-4">
            <UngdomsLogo size="lg" />
          </div>
          <CardTitle className="text-2xl font-bold text-ungdoms-800">
            √ñppenv√•rd Admin
          </CardTitle>
          <p className="text-muted-foreground">
            S√§ker inloggning f√∂r v√•rdpersonal
          </p>
        </CardHeader>

        <CardContent>
          <div className="mb-6 flex items-center justify-center space-x-2 text-sm text-ungdoms-600">
            <Shield className="h-4 w-4" />
            <span>GDPR-s√§ker v√•rdadministration</span>
          </div>

          {error && (
            <Alert className="mb-4 border-red-200 bg-red-50">
              <AlertDescription className="text-red-700">
                {error}
              </AlertDescription>
            </Alert>
          )}

          <Form {...form}>
            <form onSubmit={form.handleSubmit((data) => loginMutation.mutate(data))} className="space-y-4">
              <FormField
                control={form.control}
                name="username"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel className="text-ungdoms-700">Anv√§ndarnamn</FormLabel>
                    <FormControl>
                      <div className="relative">
                        <User className="absolute left-3 top-3 h-4 w-4 text-ungdoms-400" />
                        <Input 
                          placeholder="Skriv ditt anv√§ndarnamn" 
                          className="pl-10 border-ungdoms-200 focus:border-ungdoms-500 focus:ring-ungdoms-500"
                          {...field} 
                        />
                      </div>
                    </FormControl>
                    <FormMessage />
                  </FormItem>
                )}
              />

              <FormField
                control={form.control}
