client/src/components/quick-search.tsx.content:
import { useState, useEffect, useRef } from "react";
import { useQuery } from "@tanstack/react-query";
import { Search, User, Users, FileText, Clock } from "lucide-react";
import { Input } from "@/components/ui/input";
import { Card, CardContent } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import type { Staff, Client, CarePlan, ImplementationPlan } from "@shared/schema";

interface QuickSearchProps {
  onSelectStaff?: (staff: Staff) => void;
  onSelectClient?: (client: Client, staff: Staff) => void;
  placeholder?: string;
  className?: string;
}

interface SearchResult {
  type: 'staff' | 'client' | 'care-plan' | 'implementation-plan';
  id: string;
  title: string;
  subtitle: string;
  staff?: Staff;
  client?: Client;
  carePlan?: CarePlan;
  implementationPlan?: ImplementationPlan;
  matchedField?: string;
}

export function QuickSearch({ 
  onSelectStaff, 
  onSelectClient, 
  placeholder = "S√∂k personal, klienter eller √§renden...",
  className = ""
}: QuickSearchProps) {
  const [query, setQuery] = useState("");
  const [isOpen, setIsOpen] = useState(false);
  const [selectedIndex, setSelectedIndex] = useState(-1);
  const searchRef = useRef<HTMLDivElement>(null);
  const inputRef = useRef<HTMLInputElement>(null);

  // Fetch all data
  const { data: staff = [] } = useQuery<Staff[]>({
    queryKey: ["/api/staff"],
  });

  const { data: clients = [] } = useQuery<Client[]>({
    queryKey: ["/api/clients/all"],
  });

  const { data: carePlans = [] } = useQuery<CarePlan[]>({
    queryKey: ["/api/care-plans/all"],
  });

  const { data: implementationPlans = [] } = useQuery<ImplementationPlan[]>({
    queryKey: ["/api/implementation-plans/all"],
  });

  // Search logic
  const searchResults: SearchResult[] = query.trim().length >= 2 ? (() => {
    const results: SearchResult[] = [];
    const searchTerm = query.toLowerCase();

    // Search staff
    staff.forEach(staffMember => {
      const fields = [
        { value: staffMember.name, field: 'namn' },
        { value: staffMember.initials, field: 'initialer' },
        { value: staffMember.personnummer, field: 'personnummer' },
        { value: staffMember.telefon, field: 'telefon' },
        { value: staffMember.epost, field: 'e-post' },
        { value: staffMember.roll, field: 'roll' },
        { value: staffMember.avdelning, field: 'avdelning' }
      ];

      const matchedField = fields.find(field => 
        field.value.toLowerCase().includes(searchTerm)
      );

      if (matchedField) {
        results.push({
          type: 'staff',
          id: staffMember.id,
          title: staffMember.name,
          subtitle: `${staffMember.initials} ‚Ä¢ ${staffMember.roll || 'Personal'}`,
          staff: staffMember,
          matchedField: matchedField.field
        });
      }
    });

    // Search clients
    clients.forEach(client => {
      const staffMember = staff.find(s => s.id === client.staffId);
      if (!staffMember) return;

      const fields = [
        { value: client.initials, field: 'initialer' },
        { value: client.personalNumber, field: 'personnummer' },
        { value: client.notes, field: 'anteckningar' }
      ];

      const matchedField = fields.find(field => 
        field.value.toLowerCase().includes(searchTerm)
      );

      if (matchedField) {
        results.push({
          type: 'client',
          id: client.id,
          title: `Klient: ${client.initials}`,
          subtitle: `Ansvarig: ${staffMember.name}`,
          client,
          staff: staffMember,
          matchedField: matchedField.field
        });
      }
    });

    // Search care plans
    carePlans.forEach(carePlan => {
      const client = clients.find(c => c.id === carePlan.clientId);
      const staffMember = staff.find(s => s.id === carePlan.staffId);
      if (!client || !staffMember) return;

      const fields = [
        { value: carePlan.planContent, field: 'planinneh√•ll' },
        { value: carePlan.goals, field: 'm√•l' },
        { value: carePlan.interventions, field: 'interventioner' },
        { value: carePlan.comment, field: 'kommentar' }
      ];

      const matchedField = fields.find(field => 
        field.value.toLowerCase().includes(searchTerm)
      );

      if (matchedField) {
        results.push({
          type: 'care-plan',
          id: carePlan.id,
          title: `V√•rdplan: ${client.initials}`,
          subtitle: `${staffMember.name} ‚Ä¢ Status: ${getStatusText(carePlan.status)}`,
          client,
          staff: staffMember,
          carePlan,
          matchedField: matchedField.field
        });
      }
    });

    // Search implementation plans
    implementationPlans.forEach(implPlan => {
      const client = clients.find(c => c.id === implPlan.clientId);
      const staffMember = staff.find(s => s.id === implPlan.staffId);
      if (!client || !staffMember) return;

      const fields = [
        { value: implPlan.planContent, field: 'planinneh√•ll' },
        { value: implPlan.goals, field: 'm√•l' },
        { value: implPlan.activities, field: 'aktiviteter' },
        { value: implPlan.comment, field: 'kommentar' }
      ];

      const matchedField = fields.find(field => 
        field.value.toLowerCase().includes(searchTerm)
      );

      if (matchedField) {
        const isOverdue = new Date() > new Date(implPlan.dueDate);
        results.push({
          type: 'implementation-plan',
          id: implPlan.id,
          title: `GFP: ${client.initials}`,
          subtitle: `${staffMember.name} ‚Ä¢ ${isOverdue ? 'F√∂rsenad' : 'P√•g√•ende'}`,
          client,
          staff: staffMember,
          implementationPlan: implPlan,
          matchedField: matchedField.field
        });
      }
    });

    return results.slice(0, 8); // Limit to 8 results
  })() : [];

  // Helper function for status text
  const getStatusText = (status: string) => {
    const statusMap: Record<string, string> = {
      'received': 'Mottagen',
      'entered_journal': 'I journal',
      'staff_notified': 'Personal notifierad',
      'gfp_pending': 'V√§ntar GFP',
      'completed': 'Slutf√∂rd'
    };
    return statusMap[status] || status;
  };

  // Keyboard navigation
  useEffect(() => {
    const handleKeyDown = (e: KeyboardEvent) => {
      if (!isOpen || searchResults.length === 0) return;

      switch (e.key) {
        case 'ArrowDown':
          e.preventDefault();
          setSelectedIndex(prev => 
            prev < searchResults.length - 1 ? prev + 1 : 0
          );
          break;
        case 'ArrowUp':
          e.preventDefault();
          setSelectedIndex(prev => 
            prev > 0 ? prev - 1 : searchResults.length - 1
          );
          break;
        case 'Enter':
          e.preventDefault();
          if (selectedIndex >= 0) {
            handleSelect(searchResults[selectedIndex]);
          }
          break;
        case 'Escape':
          setIsOpen(false);
          setSelectedIndex(-1);
          inputRef.current?.blur();
          break;
      }
    };

    document.addEventListener('keydown', handleKeyDown);
    return () => document.removeEventListener('keydown', handleKeyDown);
  }, [isOpen, searchResults, selectedIndex]);

  // Close dropdown when clicking outside
  useEffect(() => {
    const handleClickOutside = (event: MouseEvent) => {
      if (searchRef.current && !searchRef.current.contains(event.target as Node)) {
        setIsOpen(false);
      }
    };

    document.addEventListener('mousedown', handleClickOutside);
    return () => document.removeEventListener('mousedown', handleClickOutside);
  }, []);

  const handleSelect = (result: SearchResult) => {
    if (result.type === 'staff' && result.staff && onSelectStaff) {
      onSelectStaff(result.staff);
    } else if (result.type === 'client' && result.client && result.staff && onSelectClient) {
      onSelectClient(result.client, result.staff);
    } else if ((result.type === 'care-plan' || result.type === 'implementation-plan') && result.client && result.staff && onSelectClient) {
      onSelectClient(result.client, result.staff);
    }

    setQuery("");
    setIsOpen(false);
    setSelectedIndex(-1);
    inputRef.current?.blur();
  };

  const getResultIcon = (type: string) => {
    switch (type) {
      case 'staff': return <User className="h-4 w-4" />;
      case 'client': return <Users className="h-4 w-4" />;
      case 'care-plan': return <FileText className="h-4 w-4" />;
      case 'implementation-plan': return <Clock className="h-4 w-4" />;
      default: return <Search className="h-4 w-4" />;
    }
  };

  const getResultTypeText = (type: string) => {
    switch (type) {
      case 'staff': return 'Personal';
      case 'client': return 'Klient';
      case 'care-plan': return 'V√•rdplan';
      case 'implementation-plan': return 'GFP';
      default: return '';
    }
  };

  return (
    <div ref={searchRef} className={`relative ${className}`}>
      <div className="relative">
        <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 h-4 w-4 text-gray-400" />
        <Input
          ref={inputRef}
          type="text"
          placeholder={placeholder}
          value={query}
          onChange={(e) => {
            setQuery(e.target.value);
            setIsOpen(true);
            setSelectedIndex(-1);
          }}
          onFocus={() => setIsOpen(true)}
          className="pl-10 pr-4"
        />
      </div>

      {isOpen && query.trim().length >= 2 && (
        <Card className="absolute top-full left-0 right-0 mt-1 z-50 max-h-96 overflow-y-auto shadow-lg border border-gray-200">
          <CardContent className="p-0">
            {searchResults.length > 0 ? (
              <div className="py-2">
                {searchResults.map((result, index) => (
                  <div
                    key={`${result.type}-${result.id}`}
                    className={`px-4 py-3 cursor-pointer border-b last:border-b-0 transition-colors ${
                      index === selectedIndex
                        ? 'bg-ungdoms-50 border-ungdoms-200'
                        : 'hover:bg-gray-50'
                    }`}
                    onClick={() => handleSelect(result)}
                  >
                    <div className="flex items-center gap-3">
                      <div className="flex-shrink-0 text-ungdoms-600">
                        {getResultIcon(result.type)}
                      </div>
                      <div className="flex-1 min-w-0">
                        <div className="flex items-center gap-2">
                          <p className="text-sm font-medium text-gray-900 truncate">
                            {result.title}
                          </p>
                          <Badge variant="secondary" className="text-xs">
                            {getResultTypeText(result.type)}
                          </Badge>
                        </div>
                        <p className="text-xs text-gray-500 truncate">
                          {result.subtitle}
                          {result.matchedField && (
                            <span className="ml-2 text-ungdoms-600">
                              (matchning: {result.matchedField})
                            </span>
                          )}
                        </p>
                      </div>
                    </div>
                  </div>
                ))}
              </div>
            ) : (
              <div className="px-4 py-6 text-center text-sm text-gray-500">
                Inga resultat hittades f√∂r "{query}"
              </div>
            )}
          </CardContent>
        </Card>
      )}

      {query.trim().length > 0 && query.trim().length < 2 && isOpen && (
        <Card className="absolute top-full left-0 right-0 mt-1 z-50 shadow-lg border border-gray-200">
          <CardContent className="p-4">
            <div className="text-center text-sm text-gray-500">
              Skriv minst 2 tecken f√∂r att s√∂ka...
            </div>
          </CardContent>
        </Card>
      )}
    </div>
  );
}

client/src/components/simple-care-plan-d.content:
import { useState } from "react";
import { useMutation } from "@tanstack/react-query";
import { queryClient } from "@/lib/queryClient";
import { useToast } from "@/hooks/use-toast";
import {
  Dialog,
  DialogContent,
  DialogHeader,
  DialogTitle,
  DialogTrigger,
} from "@/components/ui/dialog";
import { Input } from "@/components/ui/input";
import { Button } from "@/components/ui/button";
import { Label } from "@/components/ui/label";
import { FileText } from "lucide-react";

interface SimpleCarePlanDialogProps {
  trigger?: React.ReactNode;
  staffId: string;
}

export function SimpleCarePlanDialog({ trigger, staffId }: SimpleCarePlanDialogProps) {
  const [isOpen, setIsOpen] = useState(false);
  const { toast } = useToast();

  const [socialWorker, setSocialWorker] = useState("");
  const [clientInitials, setClientInitials] = useState("");
  const [planNumber, setPlanNumber] = useState("");

  const createPlan = useMutation({
    mutationFn: async () => {
      // Step 1: Create client
      const clientResp = await fetch("/api/clients", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({
          initials: clientInitials,
          staffId: staffId,
          personalNumber: "",
          notes: `V√•rdplan ${planNumber} fr√•n ${socialWorker}`,
          status: "active",
        }),
      });

      if (!clientResp.ok) throw new Error("Kunde inte skapa klient");
      const client = await clientResp.json();

      // Step 2: Create care plan
      const carePlanResp = await fetch("/api/care-plans", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({
          clientId: client.id,
          staffId: staffId,
          receivedDate: new Date().toISOString().split('T')[0],
          planContent: `V√•rdplan ${planNumber} fr√•n socialsekreterare ${socialWorker}`,
          goals: "Genomf√∂ra v√•rdfl√∂de enligt rutin",
          interventions: "Standard v√•rdfl√∂de - GFP ska p√•b√∂rjas inom 3 veckor",
          status: "staff_notified",
          comment: "",
        }),
      });

      if (!carePlanResp.ok) throw new Error("Kunde inte skapa v√•rdplan");
      const carePlan = await carePlanResp.json();

      // Step 3: Create implementation plan
      await fetch("/api/implementation-plans", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({
          clientId: client.id,
          staffId: staffId,
          carePlanId: carePlan.id,
          planContent: `GFP f√∂r v√•rdplan ${planNumber}`,
          goals: "Genomf√∂randeplan enligt v√•rdplan",
          activities: "GFP aktiviteter",
          followUpSchedule: "3 veckor",
          status: "pending",
          isActive: true,
        }),
      });

      // Step 4: Create weekly documentation
      const currentWeek = Math.max(33, Math.min(52, new Date().getWeek()));
      await fetch("/api/weekly-documentation", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({
          clientId: client.id,
          staffId: staffId,
          year: new Date().getFullYear(),
          week: currentWeek,
          content: "",
          mondayDocumented: false,
          tuesdayDocumented: false,
          wednesdayDocumented: false,
          thursdayDocumented: false,
          fridayDocumented: false,
          saturdayDocumented: false,
          sundayDocumented: false,
          documentation: "",
          approved: false,
          comments: "",
        }),
      });

      // Step 5: Create monthly report
      await fetch("/api/monthly-reports", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({
          clientId: client.id,
          staffId: staffId,
          year: new Date().getFullYear(),
          month: new Date().getMonth() + 1,
          content: "",
          reportContent: "",
          status: "not_started",
          comment: "",
        }),
      });

      // Step 6: Create Vimsa time
      await fetch("/api/vimsa-time", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({
          clientId: client.id,
          staffId: staffId,
          year: new Date().getFullYear(),
          week: currentWeek,
          monday: 0,
          tuesday: 0,
          wednesday: 0,
          thursday: 0,
          friday: 0,
          saturday: 0,
          sunday: 0,
          totalHours: 0,
          status: "not_started",
          approved: false,
          comments: "",
        }),
      });

      return { client, carePlan };
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/clients"] });
      queryClient.invalidateQueries({ queryKey: ["/api/care-plans"] });

      toast({
        title: "V√•rdplan skapad!",
        description: "Alla fl√∂den har aktiverats automatiskt.",
      });

      setIsOpen(false);
      setSocialWorker("");
      setClientInitials("");
      setPlanNumber("");
    },
    onError: (error: Error) => {
      toast({
        title: "Fel",
        description: error.message,
        variant: "destructive",
      });
    },
  });

  const handleSubmit = () => {
    if (!socialWorker || !clientInitials || !planNumber) {
      toast({
        title: "Fel",
        description: "Alla f√§lt m√•ste fyllas i",
        variant: "destructive",
      });
      return;
    }

    createPlan.mutate();
  };

  return (
    <Dialog open={isOpen} onOpenChange={setIsOpen}>
      <DialogTrigger asChild>
        {trigger || (
          <Button className="bg-ungdoms-600 hover:bg-ungdoms-700 text-white">
            <FileText className="h-4 w-4 mr-2" />
            Skapa V√•rdplan (Aktiverar Alla Fl√∂den)
          </Button>
        )}
      </DialogTrigger>
      <DialogContent className="sm:max-w-[500px]">
        <DialogHeader>
          <DialogTitle>Skapa V√•rdplan</DialogTitle>
        </DialogHeader>

        <div className="space-y-4 mt-4">
          <div>
            <Label htmlFor="social-worker">Socialsekreterare *</Label>
            <Input
              id="social-worker"
              value={socialWorker}
              onChange={(e) => setSocialWorker(e.target.value)}
              placeholder="Namn p√• socialsekreterare"
            />
          </div>

          <div>
            <Label htmlFor="client-initials">Klientinitialer *</Label>
            <Input
              id="client-initials"
              value={clientInitials}
              onChange={(e) => setClientInitials(e.target.value)}
              placeholder="T.ex. AB"
            />
          </div>

          <div>
            <Label htmlFor="plan-number">V√•rdplansnummer *</Label>
            <Input
              id="plan-number"
              value={planNumber}
              onChange={(e) => setPlanNumber(e.target.value)}
              placeholder="1, 2, 3 etc"
            />
          </div>

          <div className="bg-ungdoms-50 p-4 rounded-lg">
            <p className="text-sm text-ungdoms-700">
              N√§r du skapar v√•rdplanen aktiveras automatiskt:
            </p>
            <ul className="text-sm text-ungdoms-600 mt-2 space-y-1">
              <li>‚Ä¢ Genomf√∂randeplan (GFP)</li>
              <li>‚Ä¢ Veckodokumentation</li>
              <li>‚Ä¢ M√•nadsrapporter</li>
              <li>‚Ä¢ Vimsa Tid</li>
            </ul>
          </div>

          <Button 
            onClick={handleSubmit}
            disabled={createPlan.isPending}
            className="w-full bg-ungdoms-600 hover:bg-ungdoms-700"
          >
            {createPlan.isPending ? "Skapar..." : "Skapa V√•rdplan"}
          </Button>
        </div>
      </DialogContent>
    </Dialog>
  );
}

// Helper to get week number
declare global {
  interface Date {
    getWeek(): number;
  }
}

Date.prototype.getWeek = function() {
  const d = new Date(Date.UTC(this.getFullYear(), this.getMonth(), this.getDate()));
  const dayNum = d.getUTCDay() || 7;
  d.setUTCDate(d.getUTCDate() + 4 - dayNum);
  const yearStart = new Date(Date.UTC(d.getUTCFullYear(),0,1));
  return Math.ceil((((d.getTime() - yearStart.getTime()) / 86400000) + 1)/7);
};

client/src/components/staff-client-manag.content:
import { useState } from "react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Badge } from "@/components/ui/badge";
import { Dialog, DialogContent, DialogHeader, DialogTitle, DialogTrigger } from "@/components/ui/dialog";
import { Form, FormControl, FormField, FormItem, FormLabel, FormMessage } from "@/components/ui/form";
import { Alert, AlertDescription } from "@/components/ui/alert";
import { ClientDetailView } from "./client-detail-view";
import { CarePlanDialog } from "./care-plan-dialog";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { z } from "zod";
import { useToast } from "@/hooks/use-toast";
import { 
  Plus, 
  Users, 
  Eye, 
  Trash2, 
  CheckCircle, 
  AlertTriangle, 
  Clock,
  User,
  FileText
} from "lucide-react";
import type { Staff, Client, CarePlan, ImplementationPlan } from "@shared/schema";

interface StaffClientManagementProps {
  staff: Staff;
}

const clientSchema = z.object({
  initials: z.string().min(1, "Initialer kr√§vs").max(10, "Max 10 tecken"),
  personalNumber: z.string().optional(),
  notes: z.string().optional(),
});

type ClientFormData = z.infer<typeof clientSchema>;

export function StaffClientManagement({ staff }: StaffClientManagementProps) {
  const [selectedClient, setSelectedClient] = useState<Client | null>(null);
  const [isAddDialogOpen, setIsAddDialogOpen] = useState(false);
  const { toast } = useToast();
  const queryClient = useQueryClient();

  const form = useForm<ClientFormData>({
    resolver: zodResolver(clientSchema),
    defaultValues: {
      initials: "",
      personalNumber: "",
      notes: "",
    },
  });

  // Fetch clients for this staff member
  const { data: clients = [], isLoading: clientsLoading } = useQuery<Client[]>({
    queryKey: ["/api/staff", staff.id, "clients"],
  });

  // Fetch care plans for status overview
  const { data: carePlans = [] } = useQuery<CarePlan[]>({
    queryKey: ["/api/care-plans/staff", staff.id],
  });

  // Fetch implementation plans for status overview
  const { data: implementationPlans = [] } = useQuery<ImplementationPlan[]>({
    queryKey: ["/api/implementation-plans/staff", staff.id],
  });

  // Add client mutation
  const addClientMutation = useMutation({
    mutationFn: async (data: ClientFormData) => {
      const response = await fetch("/api/clients", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({
          ...data,
          staffId: staff.id,
          status: "active",
        }),
      });

      if (!response.ok) {
        const error = await response.json();
        throw new Error(error.message || "Kunde inte l√§gga till klient");
      }

      return response.json();
    },
    onSuccess: () => {
      toast({
        title: "Framg√•ng",
        description: "Klient tillagd framg√•ngsrikt",
      });
      form.reset();
      setIsAddDialogOpen(false);
      queryClient.invalidateQueries({ queryKey: ["/api/staff", staff.id, "clients"] });
    },
    onError: (error: Error) => {
      toast({
        title: "Fel",
        description: error.message,
        variant: "destructive",
      });
    },
  });

  // Delete client mutation
  const deleteClientMutation = useMutation({
    mutationFn: async (clientId: string) => {
      const response = await fetch(`/api/clients/${clientId}`, {
        method: "DELETE",
      });

      if (!response.ok) {
        const error = await response.json();
        throw new Error(error.message || "Kunde inte ta bort klient");
      }

      return response.json();
    },
    onSuccess: () => {
      toast({
        title: "Framg√•ng",
        description: "Klient borttagen framg√•ngsrikt",
      });
      setSelectedClient(null);
      queryClient.invalidateQueries({ queryKey: ["/api/staff", staff.id, "clients"] });
    },
    onError: (error: Error) => {
      toast({
        title: "Fel",
        description: error.message,
        variant: "destructive",
      });
    },
  });

  const handleAddClient = (data: ClientFormData) => {
    addClientMutation.mutate(data);
  };

  const handleDeleteClient = (clientId: string) => {
    if (window.confirm("√Ñr du s√§ker p√• att du vill ta bort denna klient?")) {
      deleteClientMutation.mutate(clientId);
    }
  };

  // Helper functions for status calculation
  const getClientCarePlan = (clientId: string) => {
    return carePlans.find(cp => cp.clientId === clientId);
  };

  const getClientImplementationPlan = (clientId: string) => {
    return implementationPlans.find(ip => ip.clientId === clientId);
  };

  const getClientStatusBadge = (client: Client) => {
    const carePlan = getClientCarePlan(client.id);
    const implementationPlan = getClientImplementationPlan(client.id);

    if (!carePlan) {
      return <Badge className="bg-gray-100 text-gray-800 border-gray-200">Ingen v√•rdplan</Badge>;
    }

    if (carePlan.status === "completed") {
      return <Badge className="bg-green-100 text-green-800 border-green-200"><CheckCircle className="h-3 w-3 mr-1" />Slutf√∂rd</Badge>;
    }

    if (implementationPlan) {
      const isOverdue = new Date() > new Date(implementationPlan.dueDate);
      if (isOverdue && implementationPlan.status !== "completed") {
        return <Badge className="bg-red-100 text-red-800 border-red-200"><AlertTriangle className="h-3 w-3 mr-1" />GFP F√∂rsenad</Badge>;
      }
      if (implementationPlan.status === "completed") {
        return <Badge className="bg-green-100 text-green-800 border-green-200"><CheckCircle className="h-3 w-3 mr-1" />GFP Klar</Badge>;
      }
    }

    if (carePlan.status === "staff_notified") {
      return <Badge className="bg-yellow-100 text-yellow-800 border-yellow-200"><Clock className="h-3 w-3 mr-1" />V√§ntar GFP</Badge>;
    }

    return <Badge className="bg-blue-100 text-blue-800 border-blue-200"><FileText className="h-3 w-3 mr-1" />P√•g√•ende</Badge>;
  };

  if (selectedClient) {
    return (
      <div className="space-y-4">
        <div className="flex items-center gap-4">
          <Button 
            variant="outline" 
            onClick={() => setSelectedClient(null)}
            className="border-ungdoms-200 text-ungdoms-700 hover:bg-ungdoms-50"
          >
            ‚Üê Tillbaka till klientlista
          </Button>
          <h2 className="text-xl font-semibold text-ungdoms-800">
            {staff.name} - Klient: {selectedClient.initials}
          </h2>
        </div>
        <ClientDetailView client={selectedClient} staffId={staff.id} />
      </div>
    );
  }

  return (
    <div className="space-y-6">
      {/* Staff Header */}
      <Card className="border-ungdoms-200">
        <CardHeader>
          <div className="flex items-center justify-between">
            <CardTitle className="flex items-center gap-2 text-ungdoms-800">
              <User className="h-5 w-5" />
              {staff.name} - Klienthantering
            </CardTitle>
            <Button 
              variant="outline" 
              size="sm"
              onClick={() => window.location.href = '/'}
              className="border-ungdoms-200 text-ungdoms-700 hover:bg-ungdoms-50"
            >
              ‚Üê Tillbaka till Dashboard
            </Button>
            <div className="flex gap-2">
              <CarePlanDialog trigger={
                <Button className="bg-blue-600 hover:bg-blue-700">
                  <FileText className="h-4 w-4 mr-2" />
                  Skapa V√•rdplan
                </Button>
              } />
              <Dialog open={isAddDialogOpen} onOpenChange={setIsAddDialogOpen}>
              <DialogTrigger asChild>
                <Button className="bg-green-600 hover:bg-green-700 text-white">
                  <Plus className="h-4 w-4 mr-2" />
                  L√§gg till klient
                </Button>
              </DialogTrigger>
              <DialogContent>
                <DialogHeader>
                  <DialogTitle>L√§gg till ny klient</DialogTitle>
                </DialogHeader>
                <Form {...form}>
                  <form onSubmit={form.handleSubmit(handleAddClient)} className="space-y-4">
                    <FormField
                      control={form.control}
                      name="initials"
                      render={({ field }) => (
                        <FormItem>
                          <FormLabel>Initialer (GDPR-s√§ker identifiering)</FormLabel>
                          <FormControl>
                            <Input placeholder="t.ex. A.B." {...field} />
                          </FormControl>
                          <FormMessage />
                        </FormItem>
                      )}
                    />

                    <FormField
                      control={form.control}
                      name="personalNumber"
                      render={({ field }) => (
                        <FormItem>
                          <FormLabel>Personnummer (frivilligt)</FormLabel>
                          <FormControl>
                            <Input placeholder="YYYYMMDD-XXXX" {...field} />
                          </FormControl>
                          <FormMessage />
                        </FormItem>
                      )}
                    />

                    <FormField
                      control={form.control}
                      name="notes"
                      render={({ field }) => (
                        <FormItem>
                          <FormLabel>Anteckningar</FormLabel>
                          <FormControl>
                            <Input placeholder="Ytterligare information..." {...field} />
                          </FormControl>
                          <FormMessage />
                        </FormItem>
                      )}
                    />

                    <div className="flex justify-end gap-2">
                      <Button 
                        type="button" 
                        variant="outline" 
                        onClick={() => setIsAddDialogOpen(false)}
                      >
                        Avbryt
                      </Button>
                      <Button 
                        type="submit" 
                        className="bg-green-600 hover:bg-green-700 text-white"
                        disabled={addClientMutation.isPending}
                      >
                        {addClientMutation.isPending ? "L√§gger till..." : "L√§gg till"}
                      </Button>
                    </div>
                  </form>
                </Form>
              </DialogContent>
            </Dialog>
            </div>
          </div>
        </CardHeader>
        <CardContent>
          <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
            <div>
              <p className="text-sm text-muted-foreground">Personal</p>
              <p className="font-medium">{staff.name}</p>
            </div>
            <div>
              <p className="text-sm text-muted-foreground">Avdelning</p>
              <p className="font-medium">{staff.avdelning}</p>
            </div>
            <div>
              <p className="text-sm text-muted-foreground">Antal klienter</p>
              <p className="font-medium">{clients.length}</p>
            </div>
          </div>
        </CardContent>
      </Card>

      {/* Client List */}
      <Card className="border-ungdoms-200">
        <CardHeader>
          <CardTitle className="flex items-center gap-2 text-ungdoms-800">
            <Users className="h-5 w-5" />
            Klienter ({clients.length})
          </CardTitle>
        </CardHeader>
        <CardContent>
          {clientsLoading ? (
            <div className="text-center py-8">
              <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-ungdoms-600 mx-auto mb-4"></div>
              <p className="text-muted-foreground">Laddar klienter...</p>
            </div>
          ) : clients.length === 0 ? (
            <div className="text-center py-8">
              <Users className="h-12 w-12 text-muted-foreground mx-auto mb-4" />
              <p className="text-muted-foreground">Inga klienter registrerade √§n</p>
              <p className="text-sm text-muted-foreground mt-1">
                Klicka p√• "L√§gg till klient" f√∂r att komma ig√•ng
              </p>
            </div>
          ) : (
            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
              {clients.map((client) => (
                <Card key={client.id} className="border-gray-200 hover:border-ungdoms-300 transition-colors">
                  <CardHeader className="pb-3">
                    <div className="flex items-center justify-between">
                      <CardTitle className="text-lg">{client.initials}</CardTitle>
                      {getClientStatusBadge(client)}
                    </div>
                  </CardHeader>
                  <CardContent className="pt-0">
                    <div className="space-y-2">
                      {client.notes && (
                        <p className="text-sm text-muted-foreground">{client.notes}</p>
                      )}

                      <div className="text-xs text-muted-foreground">
                        Skapad: {client.createdAt ? new Date(client.createdAt).toLocaleDateString('sv-SE') : 'Ok√§nt datum'}
                      </div>

                      <div className="flex items-center gap-2 pt-2">
                        <Button
                          size="sm"
                          variant="outline"
                          onClick={() => setSelectedClient(client)}
                          className="flex-1 border-ungdoms-200 text-ungdoms-700 hover:bg-ungdoms-50"
                        >
                          <Eye className="h-4 w-4 mr-1" />
                          Visa detaljer
                        </Button>
                        <Button
                          size="sm"
                          variant="outline"
                          onClick={() => handleDeleteClient(client.id)}
                          className="border-red-200 text-red-700 hover:bg-red-50"
                          disabled={deleteClientMutation.isPending}
                        >
                          <Trash2 className="h-4 w-4" />
                        </Button>
                      </div>
                    </div>
                  </CardContent>
                </Card>
              ))}
            </div>
          )}
        </CardContent>
      </Card>

      {/* Quick Status Overview */}
      {clients.length > 0 && (
        <Card className="border-ungdoms-200">
          <CardHeader>
            <CardTitle className="flex items-center gap-2 text-ungdoms-800">
              <FileText className="h-5 w-5" />
              Snabb status√∂versikt
            </CardTitle>
          </CardHeader>
          <CardContent>
            <div className="grid grid-cols-1 md:grid-cols-4 gap-4">
              <div className="text-center">
                <div className="text-2xl font-bold text-green-600">
                  {clients.filter(c => {
                    const cp = getClientCarePlan(c.id);
                    return cp?.status === "completed";
                  }).length}
                </div>
                <p className="text-sm text-muted-foreground">Slutf√∂rda v√•rdplaner</p>
              </div>

              <div className="text-center">
                <div className="text-2xl font-bold text-yellow-600">
                  {clients.filter(c => {
                    const cp = getClientCarePlan(c.id);
                    return cp?.status === "staff_notified";
                  }).length}
                </div>
                <p className="text-sm text-muted-foreground">V√§ntar p√• GFP</p>
              </div>

              <div className="text-center">
                <div className="text-2xl font-bold text-red-600">
                  {clients.filter(c => {
                    const ip = getClientImplementationPlan(c.id);
                    return ip && new Date() > new Date(ip.dueDate) && ip.status !== "completed";
                  }).length}
                </div>
                <p className="text-sm text-muted-foreground">F√∂rsenade GFP</p>
              </div>

              <div className="text-center">
                <div className="text-2xl font-bold text-blue-600">
                  {clients.filter(c => {
                    const ip = getClientImplementationPlan(c.id);
                    return ip?.status === "completed";
                  }).length}
                </div>
                <p className="text-sm text-muted-foreground">Klara GFP</p>
              </div>
            </div>
          </CardContent>
        </Card>
      )}

      {/* GDPR Information */}
      <Alert className="border-ungdoms-200 bg-ungdoms-50">
        <AlertTriangle className="h-4 w-4 text-ungdoms-600" />
        <AlertDescription className="text-ungdoms-700">
          <strong>GDPR-s√§kerhet:</strong> Systemet anv√§nder endast initialer f√∂r klientidentifiering. 
          Personnummer sparas krypterat och √§r frivilligt. All data hanteras enligt GDPR-best√§mmelser.
        </AlertDescription>
      </Alert>
    </div>
  );
}

