import { useState } from "react";
import { ChartLine } from "lucide-react";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { getInitials } from "@/lib/staff-data";
import type { Staff } from "@shared/schema";

interface StaffSidebarProps {
  staff: Staff[];
  activeView: string;
  onViewChange: (view: string, staffId?: string) => void;
  isOpen: boolean;
  onClose: () => void;
  searchTerm?: string;
}

export function StaffSidebar({ staff, activeView, onViewChange, isOpen, onClose, searchTerm = "" }: StaffSidebarProps) {
  const [filterTerm, setFilterTerm] = useState("");

  // Use global search term if provided, otherwise use local filter
  const effectiveSearchTerm = searchTerm || filterTerm;

  const filteredStaff = staff.filter(s => 
    s.name.toLowerCase().includes(effectiveSearchTerm.toLowerCase()) ||
    s.initials.toLowerCase().includes(effectiveSearchTerm.toLowerCase()) ||
    s.personnummer.toLowerCase().includes(effectiveSearchTerm.toLowerCase()) ||
    s.telefon.toLowerCase().includes(effectiveSearchTerm.toLowerCase()) ||
    s.epost.toLowerCase().includes(effectiveSearchTerm.toLowerCase()) ||
    s.roll.toLowerCase().includes(effectiveSearchTerm.toLowerCase()) ||
    s.avdelning.toLowerCase().includes(effectiveSearchTerm.toLowerCase())
  );

  return (
    <aside 
      className={`sidebar-transition bg-white w-80 shadow-lg border-r border-gray-200 overflow-y-auto fixed lg:static inset-y-0 left-0 z-30 ${isOpen ? '' : 'sidebar-hidden lg:transform-none'}`}
    >
      <div className="p-6">
        {/* Dashboard Link */}
        <div className="mb-6">
          <Button
            variant={activeView === 'dashboard' ? 'default' : 'outline'}
            className={`w-full justify-start ${activeView === 'dashboard' ? 'bg-blue-50 text-blue-700 border-blue-200' : ''}`}
            onClick={() => {
              onViewChange('dashboard');
              if (window.innerWidth < 1024) onClose();
            }}
          >
            <ChartLine className="mr-3 h-4 w-4" />
            Dashboard
          </Button>
        </div>

        {/* Staff Filter */}
        <div className="mb-4">
          <label className="block text-sm font-medium text-gray-700 mb-2">Filtrera personal</label>
          <Input
            type="text"
            placeholder="S√∂k personal..."
            value={filterTerm}
            onChange={(e) => setFilterTerm(e.target.value)}
          />
        </div>

        {/* Staff Tabs */}
        <div className="space-y-2">
          {filteredStaff.map((staffMember) => {
            const initials = getInitials(staffMember.name);
            const isActive = activeView === `staff-${staffMember.id}`;

            return (
              <Button
                key={staffMember.id}
                variant={isActive ? 'default' : 'ghost'}
                className={`w-full justify-start ${isActive ? 'bg-blue-50 text-blue-700 border border-blue-200' : 'text-gray-700 hover:text-gray-900'}`}
                onClick={() => {
                  onViewChange('staff', staffMember.id);
                  if (window.innerWidth < 1024) onClose();
                }}
              >
                <div className="h-8 w-8 bg-gray-200 rounded-full flex items-center justify-center mr-3">
                  <span className="text-gray-600 font-medium text-sm">{initials}</span>
                </div>
                <span className="font-medium">{staffMember.name}</span>
              </Button>
            );
          })}
        </div>
      </div>
    </aside>
  );
}

