import * as React from "react";
import { useMutation, useQuery, useQueryClient } from "@tanstack/react-query";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Form, FormControl, FormField, FormItem, FormLabel, FormMessage } from "@/components/ui/form";
import { Textarea } from "@/components/ui/textarea";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Checkbox } from "@/components/ui/checkbox";
import { useToast } from "@/hooks/use-toast";
import { insertImplementationPlanSchema, type InsertImplementationPlan, type Client } from "@shared/schema";
import { apiRequest } from "@/lib/queryClient";
import { FileCheck, Loader2 } from "lucide-react";

interface ImplementationPlanFormProps {
  client: Client;
}

export function ImplementationPlanForm({ client }: ImplementationPlanFormProps) {
  const { toast } = useToast();
  const queryClient = useQueryClient();

  // Fetch existing implementation plan for this client
  const { data: existingPlan, isLoading } = useQuery({
    queryKey: ["/api/implementation-plans", "client", client.id],
    queryFn: () => fetch(`/api/clients/${client.id}/implementation-plan`).then(res => 
      res.ok ? res.json() : null
    ),
  });

  const form = useForm<InsertImplementationPlan>({
    resolver: zodResolver(insertImplementationPlanSchema),
    defaultValues: {
      clientId: client.id,
      staffId: client.staffId || "",
      planContent: "",
      goals: "",
      activities: "",
      followUpSchedule: "",
      status: "pending",
      followup1: false,
      followup2: false,
      comments: "",
    },
  });

  // Update form when existing plan loads
  React.useEffect(() => {
    if (existingPlan) {
      form.reset({
        clientId: existingPlan.clientId,
        staffId: existingPlan.staffId,
        planContent: existingPlan.planContent || "",
        goals: existingPlan.goals || "",
        activities: existingPlan.activities || "",
        followUpSchedule: existingPlan.followUpSchedule || "",
        status: existingPlan.status,
        followup1: existingPlan.followup1,
        followup2: existingPlan.followup2,
        comments: existingPlan.comments || "",
      });
    }
  }, [existingPlan, form]);

  const savePlan = useMutation({
    mutationFn: (data: InsertImplementationPlan) => {
      const url = existingPlan 
        ? `/api/implementation-plans/${existingPlan.id}`
        : "/api/implementation-plans";
      const method = existingPlan ? "PUT" : "POST";
      return apiRequest(url, method, data);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/implementation-plans"] });
      toast({
